using namespace std;

typedef long long ll;
typedef pair<int, int> P;

#define INF 100000000
#define EPS 1e-9

#define MAX_W 50
#define MAX_H 50

int W, H;

vector<P> ans_list;

int dx[] = { 1, 0, -1, 0 };
int dy[] = { 0, -1, 0, 1 };
bool black = false, white = false;
char maze_map[MAX_H][MAX_W];

int solve(int sx, int sy, bool maze[][MAX_W]){


	maze[sy][sx] = true;

	int ans = 1;

	for (int i = 0; i < 4; i++){
		int nx = sx + dx[i];
		int ny = sy + dy[i];
		if (nx >= 0 && nx < W && ny >= 0 && ny < H){
			if (maze_map[ny][nx] == 'W')
				white = true;
			else if (maze_map[ny][nx] == 'B')
				black = true;
			else if(!maze[ny][nx]){
				ans += solve(nx, ny, maze);
			}
		}

	}

	return ans;

}

int main(){

	while (true){

		cin >> W >> H;
		if (W == 0 && H == 0)
			break;

		for (int i = 0; i < H; i++){
			for (int j = 0; j < W; j++){
				cin >> maze_map[i][j];
			}
		}

		int white_ans = 0;
		int black_ans = 0;
		bool maze[MAX_H][MAX_W];

		for (int i = 0; i < H; i++){
			for (int j = 0; j < W; j++){
				maze[i][j] = false;
			}
		}

		for (int i = 0; i < H; i++){
			for (int j = 0; j < W; j++){
				black = false, white = false;
				if (!maze[i][j] && maze_map[i][j] == '.'){
					int tmp = solve(j, i, maze);
					if (white && black)
						continue;
					else if (white)
						white_ans += tmp;
					else if (black)
						black_ans += tmp;
				}
			}
		}

		ans_list.push_back(P(black_ans, white_ans));

	}

	for (int i = 0; i < ans_list.size(); i++){
		cout << ans_list[i].first << " " << ans_list[i].second << endl;
	}

	return 0;

}

