int w, h;
VVC field;
VVB adj_white;
VVB adj_black;

void spread(char color, int sy, int sx)
{
	VVB visited = VVB(h, VB(w, false));
	queue<PII> q; q.push(MP(sy, sx));
	while (!q.empty()) {
		int y = q.front().F, x = q.front().S; q.pop();
		visited[y][x] = true;
		if (color == 'W') {
			adj_white[y][x] = true;
		} else {
			adj_black[y][x] = true;
		}
		REP (d, 4) {
			int ny = y + dy[d], nx = x + dx[d];
			IFC( !(0 <= ny && ny < h) || !(0 <= nx && nx < w) );
			if (!visited[ny][nx] && field[ny][nx] == '.') {
				q.push(MP(ny, nx));
			}
		}
	}
}

int main()
{
	std::ios_base::sync_with_stdio(false);
	while (cin >> w >> h, w | h) {
		field = VVC(h, VC(w));
		REP (i, h) {
			REP (j, w) {
				cin >> field[i][j];
			}
		}

		adj_black = VVB(h, VB(w, false));
		adj_white = VVB(h, VB(w, false));
		REP (i, h) {
			REP (j, w) {
				if (field[i][j] != '.') {
					spread(field[i][j], i, j);
				}
			}
		}

		int white = 0, black = 0;
		REP (i, h) {
			REP (j, w) {
				if (field[i][j] == '.') {
					if (adj_white[i][j] && !adj_black[i][j]) {
						++white;
					}
					if (!adj_white[i][j] && adj_black[i][j]) {
						++black;
					}
				}
			}
		}
		cout << black << " " << white << endl;
	}
}

