	if(vv[a][b]==2) f2=true;
	if(vis[a][b]) return 0;
	vis[a][b]=1;
	ret++;
	if(a>0){
		ret+=foo(a-1,b);
	}
	if(a<x-1){
		ret+=foo(a+1,b);
	}
	if(b>0){
		ret+=foo(a,b-1);
	}
	if(b<y-1){
		ret+=foo(a,b+1);
	}

	return ret;
}

