char a[55][55];
int wh[55][55], bl[55][55];

int di[] = {-1, 0, 1, 0};
int dj[] = {0, -1, 0, 1};

int main() {
    int w, h;
    while (cin >> w >> h, w | h) {
        queue<pii> q1, q2;
        for (int i = 0; i < 55; i++) {
            for (int j = 0; j < 55; j++) {
                wh[i][j] = bl[i][j] = 0;
            }
        }
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                cin >> a[j][i];
                if (a[j][i] == 'W') {
                    q1.push(pii(j, i));
                    wh[j][i] = bl[j][i] = 2;
                } else if (a[j][i] == 'B') {
                    q2.push(pii(j, i));
                    wh[j][i] = bl[j][i] = 2;
                }
            }
        }
        while (!q1.empty()) {
            int ci = q1.front().second;
            int cj = q1.front().first;
            q1.pop();
            for (int i = 0; i < 4; i++) {
                int ni = ci + di[i];
                int nj = cj + dj[i];
                if (ni < 0 || ni >= h || nj < 0 || nj >= w) continue;
                if (wh[nj][ni] == 0 && a[nj][ni] == '.') {
                    wh[nj][ni] = 1;
                    q1.push(pii(nj, ni));
                }
            }
        }
        while (!q2.empty()) {
            int ci = q2.front().second;
            int cj = q2.front().first;
            q2.pop();
            for (int i = 0; i < 4; i++) {
                int ni = ci + di[i];
                int nj = cj + dj[i];
                if (ni < 0 || ni >= h || nj < 0 || nj >= w) continue;
                if (bl[nj][ni] == 0 && a[nj][ni] == '.') {
                    bl[nj][ni] = 1;
                    q2.push(pii(nj, ni));
                }
            }
        }
        int r1 = 0, r2 = 0;
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                if (wh[j][i] == 0 && bl[j][i] == 1) r1++;
                if (wh[j][i] == 1 && bl[j][i] == 0) r2++;
            }
        }
        cout << r1 << " " << r2 << endl;
    }
    return 0;
}

