using namespace std;
const int dx[4] = {1,0,0,-1};
const int dy[4] = {0,1,-1,0};
const int B = 5;
const int W = 8;
const int BW = 13;
int flagB = 0;
int flagW = 0;
queue<pair<int,int> > que;
void dfs(vector<vector<int> > &map,int x,int y){
  map[x][y] = -1;
  que.push(pair<int,int>(x,y));
  for(int i = 0;i < 4;i++){
    if(x+dx[i] >= 0 && x+dx[i] < map.size() && y+dy[i] >= 0 && y+dy[i] < map[0].size()){
      if(map[x+dx[i]][y+dy[i]] == 1)flagB = B;
      else if(map[x+dx[i]][y+dy[i]] == 2)flagW = W;
      else if(map[x+dx[i]][y+dy[i]] == -1){}
      else dfs(map,x+dx[i],y+dy[i]);

    }
  }

  return ;
}
void solve(vector<vector<int> > &map){
  while(!que.empty()){

    pair<int,int> tmp = que.front();que.pop();
    map[tmp.first][tmp.second] = flagB+flagW;
  }
  flagB = 0;
  flagW = 0;
  return ;
}
int main(){

  while(1){
    int w,h;
    cin >> w >> h;
    if(w == 0 && h == 0)break;
    vector<vector<int> > map(w,vector<int>(h,0));
    for(int i = 0;i < h;i++){
      for(int j = 0;j < w;j++){
	char a;
	cin >> a;
	if(a == 'B')map[j][i] = 1;///B
	else if(a == 'W')map[j][i] = 2;///W
      }
    }
    for(int i = 0;i < h;i++){
      for(int j = 0;j < w;j++){
	if(map[j][i] == 0){
	  dfs(map,j,i);
	  //	  cout << flagW << "dddddddddddddd" << flagB << endl;
	  solve(map);
	}
      }
    }
    int cnt_W = 0;
    int cnt_B = 0;
    for(int i = 0;i < h;i++){
      for(int j = 0;j < w;j++){
	//cout << map[i][j] << " ";
	if(map[j][i] == B)cnt_B++;
	else if(map[j][i] == W)cnt_W++;

      }
      //      cout << endl;
    }
    cout << cnt_B << " " << cnt_W << endl;
  }
  return 0;

}

