struct Node{
    int i, j, color;
};

char island[52][52]={0};
int owner[52][52]={0};
int di[4] = {0,1,0,-1}, dj[4] = {-1,0,1,0};

void init(int w, int h){
    for(int i=1;i<=h;++i){
        for(int j=1;j<=w;++j){
            island[i][j] = '.';
            owner[i][j] = 0;
        }
    }
    for(int i=0;i<=w+1;++i){
        island[0][i] = '#';
        island[h+1][i] = '#';
    }
    for(int i=0;i<=h+1;++i){
        island[i][0] = '#';
        island[i][w+1] = '#';
    }
}

int main(){
    int w, h;
    while(cin >> w >> h, w != 0){
        init(w, h);
        queue<Node> que;
        for(int i=1;i<=h;++i){
            for(int j=1;j<=w;++j){
                cin >> island[i][j];
                if(island[i][j] == 'B'){
                    que.push((Node){i, j, 1});
                }else if(island[i][j] == 'W'){
                    que.push((Node){i, j, 2});
                }
            }
        }
        while(!que.empty()){
            Node cur = que.front(); que.pop();
            for(int k=0;k<4;++k){
                int ni = cur.i + di[k], nj = cur.j + dj[k];
