void dfs(int nowh, int noww, int bit) {
    for(int k = 0; k < 4; k++) {
        int newh = nowh + dh[k];
        int neww = noww + dw[k];
        if(newh < 1 || newh > H || neww < 1 || neww > W) continue;
        if(field[newh][neww] < 0) continue;
        if(field[newh][neww] & (1 << bit)) continue;
        field[newh][neww] |= (1 << bit);
        dfs(newh, neww, bit);
    }
}

