	vector<string>mat(h);
	for(i=0;i<h;i++){cin>>mat[i];}
	queue<tuple<int,int,char>>que;
	static int mita[100][100];
	for(i=0;i<h;i++){
		for(j=0;j<w;j++){
			mita[i][j]=0;
			if(mat[i][j]=='W'){que.push(mt(i,j,'w'));}
			if(mat[i][j]=='B'){que.push(mt(i,j,'b'));}
		}
	}
	while(que.size()){
		int y,x;char c;
		tie(y,x,c)=que.front();que.pop();
		if(x<0||y<0||x>w||y>h){continue;}
		if(c=='w'){
			if(mita[y][x]%2){continue;}
			mita[y][x]+=1;
			if(mat[y][x]=='W'){}
			else if(mat[y][x]=='.'){mat[y][x]='w';}
			else if(mat[y][x]=='b'){mat[y][x]='r';}
			else{continue;}
		}else{
			if(mita[y][x]/2){continue;}
			mita[y][x]+=2;
			if(mat[y][x]=='B'){}
			else if(mat[y][x]=='.'){mat[y][x]='b';}
			else if(mat[y][x]=='w'){mat[y][x]='r';}
			else{continue;}
		}


		if(c=='w'){
			if(y>0&&(mat[y-1][x]=='.'||mat[y-1][x]=='b'||mat[y-1][x]=='r')){que.push(mt(y-1,x,'w'));}
			if(x>0&&(mat[y][x-1]=='.'||mat[y][x-1]=='b'||mat[y][x-1]=='r')){que.push(mt(y,x-1,'w'));}
			if(y<h-1&&(mat[y+1][x]=='.'||mat[y+1][x]=='b'||mat[y+1][x]=='r')){que.push(mt(y+1,x,'w'));}
			if(x<w-1&&(mat[y][x+1]=='.'||mat[y][x+1]=='b'||mat[y][x+1]=='r')){que.push(mt(y,x+1,'w'));}
		}else{
			if(y>0&&(mat[y-1][x]=='.'||mat[y-1][x]=='w'||mat[y-1][x]=='r')){que.push(mt(y-1,x,'b'));}
			if(x>0&&(mat[y][x-1]=='.'||mat[y][x-1]=='w'||mat[y][x-1]=='r')){que.push(mt(y,x-1,'b'));}
			if(y<h-1&&(mat[y+1][x]=='.'||mat[y+1][x]=='w'||mat[y+1][x]=='r')){que.push(mt(y+1,x,'b'));}
			if(x<w-1&&(mat[y][x+1]=='.'||mat[y][x+1]=='w'||mat[y][x+1]=='r')){que.push(mt(y,x+1,'b'));}
		}
	}
	int ansW=0,ansB=0;
	for(i=0;i<h;i++){
		for(j=0;j<w;j++){
			//cerr<<mat[i][j];
			mita[i][j]=0;
			if(mat[i][j]=='w'){ansW++;}
			if(mat[i][j]=='b'){ansB++;}
		}
		//cerr<<endl;
	}
	cout<<ansB<<" "<<ansW<<endl;
	return true;
}
int main(void){
	cin.tie(0);ios::sync_with_stdio(false);
	while(solve()){}
	return 0;
}

