	for(int i=0;i<4;i++){
		if(x+X[i]>-1&&x+X[i]<w&&y+Y[i]>-1&&y+Y[i]<h){
			if(ans[x+X[i]][y+Y[i]]==0&&wh[x+X[i]][y+Y[i]]==0){white(x+X[i],y+Y[i]);}
		}
	}
}


