using namespace std;
typedef long long ll;

int f[50][50];

int dfs(vector<vector<int>> &m, int h, int w, vector<vector<char>> as, int t) {
    if (h < 0 || w < 0 || h >= m.size() || w >= m[h].size()) {
        return 0;
    }
    if (as[h][w] == 'B') return 1;
    if (as[h][w] == 'W') return 2;
    if (f[h][w]) return t;
    f[h][w] = 1;
    t |= m[h][w];
    t |= dfs(m, h-1, w, as, t);
    t |= dfs(m, h+1, w, as, t);
    t |= dfs(m, h, w+1, as, t);
    t |= dfs(m, h, w-1, as, t);
    m[h][w] = t;
    return t;
}

