bool b_flag;
bool w_flag;
int count;

void dfs1(int y, int x)
{
	if(y < 0 || h <= y || x < 0 || w <= x || field[y][x] == '#')
	{
		return;
	}
	else
	{
		if(field[y][x] == 'W')
		{
			w_flag = true;
			return;
		}
		else if(field[y][x] == 'B')
		{
			b_flag = true;
			return;
		}
		else if(field[y][x] == '.')
		{
			field[y][x] = '#';
			++count;
		}
	}
	for(int i = 0; i < 4; ++i)
	{
		int nx = x + dx[i];
		int ny = y + dy[i];
		dfs1(ny, nx);
	}
}

