{
	while(cin >> w >> h, w || h)
	{
		for(int i = 0; i < h; ++i)
		{
			for(int j = 0; j < w; ++j)
			{
				cin >> field[i][j];
			}
		}
		int b_count = 0, w_count = 0;
		for(int i = 0; i < h; ++i)
		{
			for(int j = 0; j < w; ++j)
			{
				if(field[i][j] == '.')
				{
					count = 0;
					b_flag = false;
					w_flag = false;
					dfs1(i, j);
					if(b_flag == true && w_flag == false)
					{
						b_count += count;
					}
					else if(b_flag == false && w_flag == true)
					{
						w_count += count;
					}
				}
			}
		}
		cout << b_count << " " << w_count << endl;
	}
}

