      if(a[i][j] == 'B') { qu[0].push({i, j}); }
      if(a[i][j] == 'W') { qu[1].push({i, j}); }
    }
  for(int t = 0; t < 2; ++t) {
    while(qu[t].size() > 0) {
      p now = qu[t].front();
