pair<int,char> dfs(int x,int y){
  if(x<0||y<0||x>=w||y>=h) return make_pair(0,0);
  if(a[y][x]==1) return make_pair(0,1);
  if(a[y][x]==2) return make_pair(0,2);
  if(checked[y][x]) return make_pair(0,0);
  checked[y][x]=true;
  pair<int,char> l=dfs(x-1,y);
  pair<int,char> u=dfs(x,y-1);
  pair<int,char> r=dfs(x+1,y);
  pair<int,char> d=dfs(x,y+1);
  char c=l.second|u.second|r.second|d.second;
  if(c==255||c==3) return make_pair(0,255);
  if(c!=0) return make_pair(l.first+u.first+r.first+d.first+1,c);
  return make_pair(l.first+u.first+r.first+d.first+1,0);
}

