int dfs(int xx, int yy, int n)
{
  int res = 1;
  t[yy][xx] = n;
  queue<int> q;
  q.push(xx);
  q.push(yy);

  while(q.size())
  {
    int x = q.front(); q.pop();
    int y = q.front(); q.pop();

    if(m[y][x] != '.')
    {
      if(v[n] == 0)
      {
        if(m[y][x] == 'W')
          v[n] = 1;
        else
          v[n] = 2;
      }
      if(v[n] == 1 && m[y][x] == 'B' || v[n] == 2 && m[y][x] == 'W')
        v[n] = 3;
      continue;
    }

    for(int i = 0; i < 4; i++)
    {
      int tx = x + dx[i], ty = y + dy[i];
      if(0 <= tx && tx < W && 0 <= ty && ty < H && t[ty][tx] <= 0)
      {
        q.push(tx);
        q.push(ty);
        if(!t[ty][tx])
        {
          res++;
          t[ty][tx] = n;
        }
      }
    }
  }
  return res;
}

