                B_DFS(y + dy[i], x + dx[i]);
            }
        }
    }

}

void W_DFS(int y,int x){

    if(area[y][x] == none){
        area[y][x] = white;
    }
    else if(area[y][x] == black){
        area[y][x] = eo;
    }

    for(int i=0; i<4; i++){
