using namespace std;
string grid[50];
bool used[50][50];
string surrounded[50];
int move_h[4] = {-1, 1, 0, 0};
int move_w[4] = {0, 0, -1, 1};

void dfs_fill(int cur_h, int cur_w, char color, int h, int w){
  surrounded[cur_h][cur_w] = color;
  for(int i = 0; i < 4; i++){
    int next_h = cur_h + move_h[i];
    int next_w = cur_w + move_w[i];
    if(0 <= next_h && next_h < h && 0 <= next_w && next_w < w
        && grid[next_h][next_w] == '.' && surrounded[next_h][next_w] == '.'){
      dfs_fill(next_h, next_w, color, h, w);  
    }
  }
}

char dfs_check(int cur_h, int cur_w, int h, int w){
  used[cur_h][cur_w] = true;
  if(grid[cur_h][cur_w] != '.'){
    used[cur_h][cur_w] = false;
    return grid[cur_h][cur_w];
  }
  char ret = '.';
  bool return_x = false;
  for(int i = 0; i < 4; i++){
    int next_h = cur_h + move_h[i];
    int next_w = cur_w + move_w[i];
    if(0 <= next_h && next_h < h && 0 <= next_w && next_w < w
        && !used[next_h][next_w]){
      char tmp = dfs_check(next_h, next_w, h, w);
      if(tmp == 'x' || (ret != '.' && tmp != '.' && ret != tmp)){
        return_x = true;
      }
      ret = tmp;
    }
  }
  if(return_x){
    return 'x';
  }
  return ret;
}

int main(){
  while(true){
    int w;
    int h;
    cin >> w >> h;
    if(w == 0 && h == 0){
      break;
    }
    for(int i = 0; i < h; i++){
      cin >> grid[i];
    }

    for(int i = 0; i < h; i++){
      surrounded[i] = "";
      for(int j = 0; j < w; j++){
        surrounded[i].push_back('.');
        used[i][j] = false;
      }
    }

    for(int i = 0; i < h; i++){
      for(int j = 0; j < w; j++){
        if(!used[i][j] && grid[i][j] == '.'){
          char tmp = dfs_check(i, j, h, w);
          if(tmp == 'B' || tmp == 'W'){
            dfs_fill(i, j, tmp, h, w);
          }
        }
      }
    }

    int ans_B = 0;
    int ans_W = 0;
    for(int i = 0; i < h; i++){
      for(int j = 0; j < w; j++){
        if(surrounded[i][j] == 'B'){
          ans_B ++;
        }
        if(surrounded[i][j] == 'W'){
          ans_W ++;
        }
      }
    }
    /*
    for(int i = 0; i < h; i++){
      cout << surrounded[i] << endl;
    }
    */
    cout << ans_B << " " << ans_W << endl;
  }
  return 0;
}

