void dfs(vector< vector< pair<char, int> > >& area, int curx, int cury, int t) {
	int H = area.size();
	int W = area[0].size();

	for (int i = 0; i < 4; i++) {
		int x = curx + dx[i];
		int y = cury + dy[i];

