                q.push({i,j});
                bool f = false, g = false;
                int area = 0;
                while(!q.empty()){
                    pair<int,int> now = q.front(); q.pop();
                    int y = now.first, x = now.second;
                    if(mat[y][x] != '.'){
                        if(mat[y][x] == 'W')    f = true;
                        if(mat[y][x] == 'B')    g = true;
                        continue;
                    }
                    area++;
                    mat[y][x] = 'x';
                    for(int k = 0; k < 4; k++){
                        int ny = y + dy[k];
                        int nx = x + dx[k];
                        if(inRange(ny, 0, h) && inRange(nx, 0, w) && mat[ny][nx] != 'x'){
                            q.push({ny,nx});
                        }
                    }
                }

                if(f && !g) white += area;
                if(g && !f) black += area;
            }
        }

        cout << black << " " << white << endl;

    }
    return 0;
}

