#define REP(i,m,n) for(int i=(int)(m) ; i < (int) (n) ; ++i )
#define rep(i,n) REP(i,0,n)
using ll = long long;
const int inf=1e9+7;
const ll longinf=1LL<<60 ;
const ll mod=1e9+7 ;

void solve(int w,int h){
    string s[h];
    rep(i,h)cin>>s[i];
    vector<vector<int>> dw(h,vector<int>(w,inf)),db(h,vector<int>(w,inf));
    queue<pair<int,int>> que;
    rep(i,h)rep(j,w){
        if(s[i][j]=='W'){
            dw[i][j]=0;
            que.emplace(i,j);
        }
    }
    int d[]{1,0,-1,0,1};
    while(que.size()){
        auto p=que.front();que.pop();
        int x=p.first,y=p.second;
        rep(i,4){
            int nx=x+d[i],ny=y+d[i+1];
            if(nx<0||nx>=h||ny<0||ny>=w)continue;
            if(s[nx][ny]!='.')continue;
            if(dw[nx][ny]>dw[x][y]+1){
                dw[nx][ny]=dw[x][y]+1;
                que.emplace(nx,ny);
            }
        }
    }
    rep(i,h)rep(j,w){
        if(s[i][j]=='B'){
            db[i][j]=0;
            que.emplace(i,j);
        }
    }
    while(que.size()){
        auto p=que.front();que.pop();
        int x=p.first,y=p.second;
        rep(i,4){
            int nx=x+d[i],ny=y+d[i+1];
            if(nx<0||nx>=h||ny<0||ny>=w)continue;
            if(s[nx][ny]!='.')continue;
            if(db[nx][ny]>db[x][y]+1){
                db[nx][ny]=db[x][y]+1;
                que.emplace(nx,ny);
            }
        }
    }
    int aw=0,ab=0;
    rep(i,h)rep(j,w){
        if(dw[i][j]!=inf&&db[i][j]==inf&&s[i][j]!='W')++aw;
        if(dw[i][j]==inf&&db[i][j]!=inf&&s[i][j]!='B')++ab;
    }
    cout<<ab<<" "<<aw<<endl;
}

int main(){
    int w,h;
    while(cin>>w>>h,w!=0)solve(w,h);
    return 0;
}

