using namespace std;

char map[50][50];
bool memoB[50][50];
bool memoW[50][50];

void search(int x, int y, int w, int h, char color) {
  if (color == 'B') {
    memoB[x][y] = true;
  } else {
    memoW[x][y] = true;
  }
  if (x > 0 && map[x-1][y] == '.') {
    if ((color == 'B' && !memoB[x-1][y]) || (color == 'W' && !memoW[x-1][y])) {
      search(x-1, y, w, h, color);
    }
  }
  if (y > 0 && map[x][y-1] == '.') {
    if ((color == 'B' && !memoB[x][y-1]) || (color == 'W' && !memoW[x][y-1])) {
      search(x, y-1, w, h, color);
    }
  }
  if (x < w-1 && map[x+1][y] == '.') {
    if ((color == 'B' && !memoB[x+1][y]) || (color == 'W' && !memoW[x+1][y])) {
      search(x+1, y, w, h, color);
    }
  }
  if (y < h-1 && map[x][y+1] == '.') {
    if ((color == 'B' && !memoB[x][y+1]) || (color == 'W' && !memoW[x][y+1])) {
      search(x, y+1, w, h, color);
    }
  }
}

int main() {
  int w, h, cntB, cntW, resB, resW;
  char tmp;
  while (cin >> w >> h, w | h) {
    cntB = cntW = 0;
    for (int i = 0; i < w; i++) {
      for (int j = 0; j < h; j++) {
        cin >> tmp;
        map[i][j] = tmp;
        if (tmp == '.') memoB[i][j] = false; memoW[i][j] = false;
        if (tmp == 'B') memoB[i][j] = true, memoW[i][j] = false, cntB++;
        if (tmp == 'W') memoB[i][j] = false, memoW[i][j] = true, cntW++;
      }
    }
    for (int i = 0; i < w; i++) {
      for (int j = 0; j < h; j++) {
        if (map[i][j] == 'B') search(i, j, w, h, 'B');
        if (map[i][j] == 'W') search(i, j, w, h, 'W');
      }
    }
    resB = resW = 0;
    for (int i = 0; i < w; i++) {
      for (int j = 0; j < h; j++) {
        if (memoB[i][j] && !memoW[i][j]) resB++;
        if (!memoB[i][j] && memoW[i][j]) resW++;
      }
    }
    cout << resB-cntB << " " << resW-cntW << el;
  }
}

#include "bits/stdc++.h"
using namespace std;

vector<vector<int>>field;
vector<vector<bool>>used;


int dx[4] = { -1,0,1,0 };
int dy[4] = { 0,1,0,-1 };

pair<int, int>getans(const int y, const int x) {
	if (used[y][x]) {
		return make_pair(field[y][x]?field[y][x]:3, 0);
	}
	used[y][x] = true;
	if (field[y][x]) {
		return make_pair(field[y][x], 0);
	}
	int nowcolor = 3;
	int nowcount = 1;
	for (int i = 0; i < 4; ++i) {
		pair<int, int>p(getans(y + dy[i], x + dx[i]));
		nowcolor &= p.first;
		nowcount += p.second;

	}
	return make_pair(nowcolor, nowcount);
}

int main() {
	while (1) {
		int w, h; cin >> w >> h;
		field.clear();
		used.clear();
		field.resize(h + 2);
		used.resize(h + 2);
		for (int i = 0; i < h + 2; ++i) {
			used[i].resize(w + 2);
		}
		for (int i = 0; i < w + 2; ++i) {
			field[0].push_back(3);
		}
		if (!w)break;
		for (int i = 0; i < h; ++i) {
			field[i + 1].push_back(3);
			string st; cin >> st;
			for (int j = 0; j < w; ++j) {
				if (st[j] == '.') {
					field[i + 1].push_back(0);
				}
				else if (st[j] == 'W') {
					field[i + 1].push_back(1);
				}
				else {
					field[i + 1].push_back(2);
				}
			}
			field[i + 1].push_back(3);
		}
		for (int i = 0; i < w + 2; ++i) {
			field[h+1].push_back(3);
		}
		int whnum = 0;
		int blnum = 0;
		for (int y = 0; y < h + 2; ++y) {
			for (int x = 0; x < w + 2; ++x) {
				if (!used[y][x]) {
					pair<int, int>a(getans(y, x));
					if (a.first == 1)whnum+=a.second;
					if (a.first == 2)blnum+= a.second;

				}
			}
		}
		cout << blnum << " " <<whnum << endl;
	}
	return 0;
}

//Surrounding Area
#include<bits/stdc++.h>
using namespace std;
#define TMP 9999
#define INF 1000

int n, m;
vector<vector<int> > board;

//4方向
int dir[4][2]={{-1,0}, {0,1}, {1,0}, {0,-1}};

void print(){
  for(int i=0; i<n; i++){
    for(int j=0; j<m; j++)
      printf("%5d", board[i][j]);
    puts("");
  }
}

bool solve(int x, int y, int c){
  if(!(0<=x && x<n && 0<=y && y<m))return true;//枠外
  if(board[x][y]==TMP)return true;//保留中
  if(board[x][y]==INF)return false;//誰の領土でもない
  if(board[x][y]==c)return true;
  if(board[x][y]==-c)return false;
  if(board[x][y]==2*c)return true;
  board[x][y]=TMP;
  bool flag=true;
  for(int i=0; i<4; i++){
    if(!solve(x+dir[i][0], y+dir[i][1], c)){
      flag=false;
      break;
    }
  }
  if(flag){
    board[x][y]=c*2;
    return true;
  }
  else{
    board[x][y]=INF;
    return false;
  }
}

int main(){
  while(true){
    cin>>n>>m;
    board.clear();
    if(n==0 && m==0)break;
    for(int i=0; i<n; i++){
      vector<int> tmp;
      for(int j=0; j<m; j++){
        char c;
        cin>>c;
        int t;
        if(c=='.')t=0;
        if(c=='W')t=1;
        if(c=='B')t=-1;
        tmp.push_back(t);
      }
      board.push_back(tmp);
    }

    for(int i=0; i<n; i++){
      for(int j=0; j<m; j++){
        if(board[i][j]==1){
          for(int k=0; k<4; k++)solve(i+dir[k][0], j+dir[k][1], 1);
        }
        if(board[i][j]==-1){
          for(int k=0; k<4; k++)solve(i+dir[k][0], j+dir[k][1], -1);
        }
      }
    }

    int nw=0, nb=0;
    for(int i=0; i<n; i++)
      for(int j=0; j<m; j++)
        if(board[i][j]==2)nw++;
        else if(board[i][j]==-2)nb++;
    printf("%d %d\n", nb, nw);
  }
}

