int dr[] = { 1, 0, -1, 0 };
int dc[] = { 0, 1, 0, -1 };

int main() {
    int w, h;
    while (cin >> w >> h) {
        if (w == 0 and h == 0) break;
        string board[51];
        rep(r, h) cin >> board[r];
        int wc = 0, bc = 0;
        rep(r, h) rep(c, w) {
            if (board[r][c] != 'B' and board[r][c] != 'W') continue;
            queue<P> que;
            que.push(make_pair(r, c));
            bitset<50*50+1> visited;
            visited[r*w+c] = 1;
            while (que.size()) {
                P p = que.front();
                que.pop();
                rep(i, 4) {
                    int nr = p.first + dr[i], nc = p.second + dc[i];
                    if (nr < 0 or nr >= h or nc < 0 or nc >= w or visited[nr*w+nc] == 1) continue;
                    if (board[nr][nc] == 'W' or board[nr][nc] == 'B') continue;
                    if (board[nr][nc] == '.') {
                        if (board[r][c] == 'W') board[nr][nc] = 'w';
                        if (board[r][c] == 'B') board[nr][nc] = 'b';
                    }
                    else {
                        if (board[r][c] == 'W' and board[nr][nc] == 'b') board[nr][nc] = 'x';
                        if (board[r][c] == 'B' and board[nr][nc] == 'w') board[nr][nc] = 'x';
                    }
                    visited[nr*w+nc] = 1;
                    que.push(make_pair(nr, nc));
                }
            }
        }
        rep(rr, h) rep(cc, w) {
            if (board[rr][cc] == 'w') wc++;
            else if (board[rr][cc] == 'b') bc++;
        }
        cout << bc << " " << wc << endl;
    }
    return 0;
}

