void search(int x, int y, int z){
	pair<int, int> r;
	if(a[x][y] == '.')f[z]++;
	if(a[x+1][y]=='.'&&c[z][x+1][y]==0){
		c[z][x+1][y] = 1;
		r.first = x+1;
		r.second = y;
		q[z].push(r);
	}
	if(a[x-1][y]=='.'&&c[z][x-1][y]==0){
		c[z][x-1][y] = 1;
		r.first = x-1;
		r.second = y;
		q[z].push(r);
	}
	if(a[x][y-1]=='.'&&c[z][x][y-1]==0){
		c[z][x][y-1] = 1;
		r.first = x;
		r.second = y-1;
		q[z].push(r);
	}
	if(a[x][y+1]=='.'&&c[z][x][y+1]==0){
		c[z][x][y+1] = 1;
		r.first = x;
		r.second = y+1;
		q[z].push(r);
	}
	q[z].pop();
	if(!q[z].empty()) search(q[z].front().first, q[z].front().second, z);
}

