#include <iostream>
#include <vector>
#include <stack>



void dfs(std::vector<std::vector<char> > &map, int i, int j, char self_color, char other_color, int &total) {
    int first;
    int second;

    int count = 0;
    bool check_surrounded = true;
    bool check_only_wall = false;

    int dx[] = {0, 0, 1, -1};
    int dy[] = {1, -1, 0, 0};

    std::stack<std::pair<int, int> > st;
    st.push(std::make_pair(i, j));
    map[i][j] = '$';

    while (1) {
        if (st.empty() == 1) {
            break; 
        }
        first = st.top().first;
        second = st.top().second;
        st.pop();
        ++count;
        for (int k = 0; k < 4; k++) {
            if (map[first + dx[k]][second + dy[k]] == '.') {
                st.push(std::make_pair(first + dx[k], second + dy[k])); 
                map[first + dx[k]][second + dy[k]] = '$';
            } else if (map[first + dx[k]][second + dy[k]] == other_color) {
                check_surrounded = false; 
            }
            if (map[first + dx[k]][second + dy[k]] == self_color) {
                check_only_wall = true;
            }
        }
    }
    if (check_surrounded == true && check_only_wall == true) {
        total += count;
    } else {
        check_surrounded = true;
        check_only_wall = false;
    }
    return;
}

int whole_search(std::vector<std::vector<char> > map, int w, int h, char self_color, char other_color) {
    int total = 0;

    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            if (map[i][j] == '.') {
                dfs(map, i, j, self_color, other_color, total);
            }
        }
    }
    return total; 
}

int main(void) {
    int w;
    int h;

    while (1) {
        std::cin >> w >> h;
        if (w == 0 && h == 0) {
            break;
        }

        std::vector<std::vector<char> > map_b(h+2, std::vector<char>(w+2, '#'));
        std::vector<std::vector<char> > map_w(h+2, std::vector<char>(w+2, '#'));

        for (int i = 1; i <= h; i++) {
            for (int j = 1; j <= w; j++) {
                std::cin >> map_b[i][j];
                map_w[i][j] = map_b[i][j];
            }
        }
        std::cout << whole_search(map_b, w, h, 'B', 'W') << " " << whole_search(map_w, w, h, 'W', 'B') << std::endl;
    }


    return 0;
}

#include <iostream>
#include <queue>
#include <map>
using namespace std;
typedef pair<int ,int> P;
char grid[50][50];
int W,H;
int dx[] = {0,0,1,-1},dy[] = {1,-1,0,0};
char c[2] = {'B','W'};

int bfs(int sx,int sy,int k){
  queue<P> que;
  int res[2] = {0};
  bool flag = true;
  que.push(P(sx,sy));
  while(!que.empty()){
    P p = que.front();
    que.pop(); 

    if(grid[p.first][p.second] == '.'){
      grid[p.first][p.second] = c[k];
      res[k]++;
    } 
    for(int i = 0 ; i < 4 ; i++ ){
      int nx = p.first + dx[i],ny = p.second + dy[i];
      if(0 <= nx && nx < W && 0 <= ny && ny < H && grid[nx][ny] == '.'){
	que.push(P(nx,ny));
	if((nx+1 < W && grid[nx+1][ny] == c[!k])||
	   (nx-1 >= 0 && grid[nx-1][ny] == c[!k])||
	   (ny+1 < H && grid[nx][ny+1] == c[!k])||
	   (ny-1 >= 0 && grid[nx][ny-1] == c[!k]))flag = false;
      }
    }
  }
  if(flag == true)return res[k];
  else return 0; 
}

int main(){
  while( cin >> W >> H , W+H ){

    for(int i = 0 ; i < H ; i++ ){
      for(int j = 0 ; j < W ; j++ ){
	cin >> grid[j][i];
      }
    }
    int ans[2] = {0};
    for(int k = 0 ; k < 2 ; k++ ){
      for(int i = 0 ; i < H ; i++ ){
	for(int j = 0 ; j < W ; j++ ){
	  if(grid[j][i] == '.'){
	    if((j+1 < W && grid[j+1][i] == c[k])||
	       (j-1 >= 0 && grid[j-1][i] == c[k])||
	       (i+1 < H && grid[j][i+1] == c[k])||
	       (i-1 >= 0 && grid[j][i-1] == c[k]))ans[k]+=bfs(j,i,k);
	  }
	}
      }
    }
    cout << ans[0] <<" "<< ans[1] << endl;
  }
  return 0;
}

