int check(int x, int y, char a, int wb)
{
    if(F[x-1][y][wb] == '.')
    {
        F[x-1][y][wb] = a;
        check(x-1,y,a,wb);
    }
    if(F[x+1][y][wb] == '.')
    {
        F[x+1][y][wb] = a;
        check(x+1,y,a,wb);
    }
    if(F[x][y-1][wb] == '.')
    {
        F[x][y-1][wb] = a;
        check(x,y-1,a,wb);
    }
    if(F[x][y+1][wb] == '.')
    {
        F[x][y+1][wb] = a;
        check(x,y+1,a,wb);
    }
}
int main()
{
    int flag;
