    char a, b;
    while(cin >> w >> h && w && h)
    {
        nw = 0; nb = 0;
        for(int j=1;j<=h;++j){
            for(int i=1;i<=w;++i){
                cin >> F[i][j][0];
                F[i][j][1] = F[i][j][0];
            }
        }
        for(int k=0;k<2;++k){
            if(k==0) a='w', b='W';
            if(k==1) a='b', b='B';
            for(int j=1;j<=h;++j){
                for(int i=1;i<=w;++i){
                    if(F[i][j][k] == b) check(i,j,a,k);
