int main(){
	while(cin >> w >> h, w | h){
		memset(b, 0, sizeof(b));
		memset(wh, 0, sizeof(wh));
		queue<P> black, white;
		for(int i = 0;i < h;i++){
			for(int j = 0;j < w;j++){
				cin >> a[i][j];
				if(a[i][j] == 'B')black.push(P(i, j));
				if(a[i][j] == 'W')white.push(P(i, j));
			}
		}
		while(!black.empty()){
			for(int t = black.size() - 1;t >= 0;t--){
				P tmp = black.front();black.pop();
