      if(i==0) continue;
      lp(j,w+1){
	if(j==0) continue;
	cin>>a[j][i];
      }
    }
    calc(w,h);
    int countb=0,countw=0;
    lp(i,h+1){
      if(i==0) continue;
      lp(j,w+1){
	if(j==0) continue;
