void bfs (int x, int y, char s, char t, char u) {
    queue<P> q;
    q.push(P(x,y));

    while (q.size()) {
        P p = q.front();
        q.pop();
        for (int i = 0; i < 4; i++) {
            int x = p.first + dx[i], y = p.second + dy[i];
            if (0 <= x && x < m && 0 <= y && y < n) {
                if (d[x][y] == '.') {
                    if (s == 'W') w++;
                    else b++;
                    q.push(P(x,y));
                    d[x][y] = t;
                } else if (d[x][y] == u) {
                    if (s == 'W') b--;
                    else w--;
                    q.push(P(x,y));
                    d[x][y] = t;
                }
            }
        }
    }
}

