int bfs(int sx,int sy,int k){
  queue<P> que;
  int res[2] = {0};
  bool flag = true;
  que.push(P(sx,sy));
  while(!que.empty()){
    P p = que.front();
    que.pop(); 
    if(grid[p.first][p.second] == '.'){
      grid[p.first][p.second] = c[k];
      res[k]++;
      cout << res[k] << endl;
    }
    for(int i = 0 ; i < 4 ; i++ ){
      int nx = p.first + dx[i],ny = p.second + dy[i];
      if(0 <= nx && nx < W && 0 <= ny && ny < H && grid[nx][ny] == '.'){
	que.push(P(nx,ny));
	if((nx+1 < W && grid[nx+1][ny] == c[!k])||
	   (nx-1 >= 0 && grid[nx-1][ny] == c[!k])||
	   (ny+1 < H && grid[nx][ny+1] == c[!k])||
	   (ny-1 >= 0 && grid[nx][ny-1] == c[!k]))flag = false;
      }
    }
  }
  if(flag == true)return res[k];
  else return 0; 
}

