#include <queue>
using namespace std;

typedef pair<int,int> P;

int w = 0, b = 0;
int n, m;
char d[50][50];
const int dx[] = {0,1,0,-1}, dy[] = {1,0,-1,0};

void bfs (int x, int y, char s) {
    queue<P> q;
    q.push(P(x,y));

    while (q.size()) {
        P p = q.front();
        q.pop();
        for (int i = 0; i < 4; i++) {
            int x = p.first + dx[i], y = p.second + dy[i];
            if (0 <= x && x < m && 0 <= y && y < n) {
                if (d[x][y] == '.') {
                    d[x][y] = s == 'W' ? '@' : '*';
                    s == 'W' ? b++ : w++;
                    q.push(P(x,y));
                } else if (d[x][y] == (s == 'W' ? '*' : '@')) {
                    d[x][y] = s == 'W' ? '@' : '*';
                    s == 'W' ? w-- : b--;
                    q.push(P(x,y));
                }
            }
        }
    }
}

int main ()
{
    while (cin >> n >> m, (n || m)) {
        for (int i = 0; i < m; i++) {
