        }

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (d[i][j] == 'W') {
                    bfs(i, j, 'W');
                }
            }
        }
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (d[i][j] == 'B') {
                    bfs(i, j, 'B');
                }
            }
        }
        cout << w << " " << b << endl;
        w = b = 0;
    }

    return 0;
}

