bool chk_b(int x,int y){
  if (black[x][y] == 1)
    return true;
  if (black[x][y] < 0)
    return false;
  if (black[x][y] == 0){
    black[x][y] = -1;
    if (chk_b(x-1,y)||chk_b(x+1,y)||chk_b(x,y+1)||chk_b(x,y-1)){
      black[x][y] = 1;
      return true;
    }
    else{
      black[x][y] = 0;
      return false;
    }
  }
}

bool chk_w(int x,int y){
  if (white[x][y] == 1)
    return true;
  if (white[x][y] < 0)
    return false;
  if (white[x][y] == 0){
    white[x][y] = -1;
    if (chk_w(x-1,y)||chk_w(x+1,y)||chk_w(x,y+1)||chk_w(x,y-1)){
      white[x][y] = 1;
      return true;
    }
    else{
      white[x][y] = 0;
      return false;
    }
  }
}

