void solve(int x,int y,char c){
	char c_ , r ;
	if( c == 'W' ){
		c_ = 'w';
		r = 'B';
	}
	if( c == 'B'){
		c_ = 'b';
		r = 'W';
	}

	for(int i=0 ; i<4 ; ++i ){
		int mx = x + dx[i];
		int my = y + dy[i];

		if( mx < 0 || my < 0 || mx >= w || my >= h ) continue;

		if( c == 'W' || c == 'B'){
			if( f[my][mx] == '.' ){
				f[my][mx] = c_ ;
				solve( mx , my , c );
			}
			if( f[my][mx] == r ){
				f[my][mx] = 'x' ;
				solve( mx , my , 'x' );
			}
		}else if( c == 'x' ){
			if( f[my][mx] == 'w' || f[my][mx] == 'b' || f[my][mx] == '.' ){
				f[my][mx] = 'x';
				solve( mx , my , 'x' );
			}
		}
	}
}

