int W, H;
vector<string> m;
bool inRange(int x, int y) {
	return 0 <= x && x < W && 0 <= y && y < H;
}
const int dx[] = {0, 1, 0, -1};
const int dy[] = {-1, 0, 1, 0};
set<char> check(int x, int y, vector< vector<bool> >& checked) {
	set<char> res;
	if ( !inRange(x, y) ) return res;
	if (checked[y][x]) return res;
	checked[y][x] = true;
	if ( m[y][x] != '.' ) {
		res.insert(m[y][x]);
		return res;
	}
	REP(i, 4) {
		set<char> s = check(x+dx[i], y+dy[i], checked);
		EACH(e, s) res.insert(e);
	}
	return res;
}
int main() {
	std::ios::sync_with_stdio(false);
	std::cin.tie(0);
	while ( cin >> W >> H, W || H ) {
		m = vector<string>(H); cin >> m;
		map<char, int> ans;
		ans['W'] = 0;
		ans['B'] = 0;
		REP(y, H) REP(x, W) {
			switch (m[y][x]) {
				case 'W': break;
				case 'B': break;
				case '.':
				{
					vector< vector<bool> > checked(H, vector<bool>(W, false));
					set<char> s = check(x, y, checked);
					if (s.size() == 1) {
						ans[*s.begin()]++;
					}
				}
			}
		}
		cout << ans['B'] << " " << ans['W'] << endl;
	}
}

