int dx[8] = {0, 1, 0, -1, 1, -1, 1, -1};
int dy[8] = {1, 0, -1, 0, 1, -1, -1, 1};

struct Status {
  bool b, w;
  Status() : b(false), w(false) {}
};

char a[51][51];
int w, h;

int dfs(int y, int x, Status &s) {
  if (a[y][x] == 'W') {
    s.w = true;
    return 0;
  }
  if (a[y][x] == 'B') {
    s.b = true;
    return 0;
  }

  int count = 1;
  a[y][x] = 'D';
  rep(i, 4) {
    int nx = x + dx[i];
    int ny = y + dy[i];

    if (0 <= nx && nx < w && 0 <= ny && ny < h && a[ny][nx] != 'D') {
      count += dfs(ny, nx, s);
    }
  }

  return count;
}

int main(void) {
  ios_base::sync_with_stdio(false); cin.tie(0);
  while (cin >> w >> h, w || h) {
    rep(i, h) rep(j, w) cin >> a[i][j];

    int cnt_w = 0;
    int cnt_b = 0;

    rep(i, h) rep(j, w) {
      if (a[i][j] == '.') {
        Status s;
        int count = dfs(i, j, s);
        if (s.w && s.b) continue;
        if (s.w) cnt_w += count;
        if (s.b) cnt_b += count;
      }
    }

    cout << cnt_b << " " << cnt_w << endl;
  }
  return 0; 
}

#include <bits/stdc++.h>

using namespace std;

int w, h;
vector<string> feeld;

int dx[4] = {0, 1, 0, -1};
int dy[4] = {1, 0, -1, 1};

int add, id;
void rec(int x, int y) {
	if (feeld[y][x] == 'B') {
		id |= 1;
		return;
	}

	if (feeld[y][x] == 'W') {
		id |= 2;
		return;
	}

	feeld[y][x] = '-';
	add++;

	for (int i = 0; i < 4; i++) {
		int nx = x + dx[i], ny = y + dy[i];
		if (!(0 <= nx && nx < w && 0 <= ny && ny < h)) continue;
		if (feeld[ny][nx] == '-') continue;
		rec(nx, ny);
	}
}

void solve() {
	int ansb = 0, answ = 0;

	for (int i = 0; i < h; i++) {
		for (int j = 0; j < w; j++) {
			if (feeld[i][j] == '.') {
				add = 0; id = 0;
				rec(j, i);
				if (id == 1) ansb += add;
				if (id == 2) answ += add;
			}
		}
	}

	cout << ansb << " " << answ << endl;
}

int main() {
	while (1) {
		cin >> w >> h; if (w == 0 && h == 0) break;
		feeld.assign(h, "");
		for (int i = 0; i < h; i++) cin >> feeld[i];

		solve();
	}

	return 0;
}

#include <bits/stdc++.h>
#define rep(i,n) for(int i=0;i<(n);i++)
using namespace std;
typedef pair <int,int> pii;
#define FST first
#define SEC second
#define S 50

int w,h;
int dd[8]={0,1,0,-1,0};

void bfs(char map[S][S]){
  int bc=0,wc=0;
  rep(i,h){
    rep(j,w){
      if(map[j][i]=='.'){
	queue <pii> q;
	q.push(pii(j,i));
	int count=0;
	pii state=pii(0,0);
	while(!q.empty()){
	  int cx=q.front().FST,cy=q.front().SEC;
	  q.pop();
	  if(map[cx][cy]=='.')
	    count++;
	  map[cx][cy]='#';
	  rep(r,4){
	    int nx=cx+dd[r],ny=cy+dd[r+1];
	    if(nx<0 || ny<0 || nx>=w || ny>=h || map[nx][ny]=='#')
	      continue;
	    if(map[nx][ny]=='B'){
	      state.FST=1;
	      continue;
	    }
	    if(map[nx][ny]=='W'){
	      state.SEC=1;
	      continue;
	    }
	    q.push(pii(nx,ny));
	  }
	}
	if(state==pii(1,0))
	  bc+=count;
	if(state==pii(0,1))
	  wc+=count;
      } 
    }
  }
  cout << bc << " " << wc << endl;
}

int main(){
  for(;;){
    cin >> w >> h;
    if(!(w+h)) break;
    char map[S][S];
    rep(i,h)
      rep(j,w)
        cin >> map[j][i];
    bfs(map);
  }
}

