void solve(int y,int x,char c){
    if((c == 'B' && black[y][x]) || (c == 'W' && white[y][x])) return;
    if(c == 'W' && map[y][x]!='W') white[y][x] = 1;
    else if(c == 'B' && map[y][x]!='B') black[y][x] = 1;

    rep(i, 4){
