/* dir := down->right->up->left */

int is_area(int x, int y)
{
  return (0 <= x && x < w && 0 <= y && y < h && area[y][x] != 'V');
}

void dfs(int x, int y)
{
  int i, j, dir_x, dir_y;
  count++;
  area[y][x] = 'V';

  for(i = 0; i < DIR_N; i++)
    {
      dir_x = x + dir[i][0];
      dir_y = y + dir[i][1];
      if(is_area(dir_x, dir_y))
	{
	  if(area[dir_y][dir_x] == 'W')
	    {
	      if(flag == 0)
		{
		  flag = 1;
		}
	      if(flag == 2)
		{
		  flag = 3;
		}
	      continue;
	    }
	  if(area[dir_y][dir_x] == 'B')
	    {
	      if(flag == 0)
		{
		  flag = 2;
		}
	      if(flag == 1)
		{
		  flag = 3;
		}
	      continue;
	    }
	  dfs(dir_x, dir_y);
	}
    }
}

