int measure(int x,int y){
  int i,m=0,dir[4][2]={{1,0},{-1,0},{0,1},{0,-1}},nx,ny;
  map[y][x]='F';
  for(m=1,i=0;i<4;i++){
    nx=x+dir[i][1];
    ny=y+dir[i][0];
    if(nx<we&&nx>-1&&ny<he&&ny>-1){
      if(map[ny][nx]=='.'){
	m+=measure(nx,ny);
      }else if(map[ny][nx]=='B'){
	b++;
      }else if(map[ny][nx]=='W'){
	w++;
      }
    }
  }
  return m;
}
int main(){
