    for(i=white=black=0;i<he;i++){
      for(j=0;j<we;j++){
	if(map[i][j]=='.'){
	  m=measure(j,i);
	  if(w&&!b){
	    white+=m;
	  }else if(!w&&b){
	    black+=m;
	  }
	  w=b=0;
	}
      }
    }
    printf("%d %d\n",black,white);
  }while(1);
  return 0;
}

