int a[51][51],w,h,c;
bool f;

void visit(int i,int j,int bw){
  a[i][j] = 3;
  c++;
  int di[4] = {1,0,-1,0};
  int dj[4] = {0,1,0,-1};
  for(int t=0;t<4;t++){
    int vi = i + di[t];
    int vj = j + dj[t];
    if(bw == 1){
      if(a[vi][vj] == 2){
	f = false;
      }
      else if(vi >= 0 && vj >= 0 && vi < w && vj < h && !a[vi][vj]){
	visit(vi,vj,1);
      }
    }
    else if(bw == 2){
      if(a[vi][vj] == 1){
	f = false;
      }
      else if(vi >= 0 && vj >= 0 && vi < w && vj < h && !a[vi][vj]){
	visit(vi,vj,2);
      }
    }
  }
}

