import java.io.*;
import java.util.*;

public class Main {
	FastScanner in = new FastScanner(System.in);
	PrintWriter out = new PrintWriter(System.out);

	boolean black, white;
	char[][] map;
	boolean[][] visited;

	int[] vx = {-1, 0, 0, 1};
	int[] vy = {0, -1, 1, 0};

	int dfs(int x, int y) {
		if (visited[y][x]) return 0;
		if (map[y][x] == 'B') {
			black = true;
			return 0;
		} else if (map[y][x] == 'W') {
			white = true;
			return 0;
		}

		visited[y][x] = true;
		int res = 1;

		for (int d = 0; d < vx.length; d++) {
			int nx = x + vx[d], ny = y + vy[d];
			if (nx >= 0 && nx < map[0].length && ny >= 0 && ny < map.length) {
				res += dfs(nx, ny);
			}
		}
		return res;
	}

	public void run() {

		while (true) {
			int m = in.nextInt(), n = in.nextInt();
			if (n == 0) break;

			map = new char[n][m];
			for (int i = 0; i < n; i++) {
				map[i] = in.next().toCharArray();
			}

			int wres = 0, bres = 0;
			visited = new boolean[n][m];
			for (int i = 0; i < n; i++) {
				for (int j = 0; j < m; j++) {
					if (!visited[i][j])	{
						white = false;
						black = false;
						int cnt = dfs(j, i);
						if (white && !black) wres += cnt;
						if (!white && black) bres += cnt;
					}
				}
			}
			System.out.println(bres + " " + wres);
		}
		out.close();
	}

	public static void main(String[] args) {
		new Main().run();
	}

