public class Main{

static PrintWriter out;
static InputReader ir;

static void solve(){
 for(;;){
  int w=ir.nextInt();
  int h=ir.nextInt();
  if(w==0&&h==0) return;
  char[][] map=ir.nextCharMap(h,w);
  int[][] cnd=new int[h][w];
  for(int i=0;i<h;i++){
   for(int j=0;j<w;j++){
    if(map[i][j]!='.') bfs(i,j,map[i][j]=='B'?1:2,map,cnd);
   }
  }
  int bl=0,wh=0;
  for(int i=0;i<h;i++){
   for(int j=0;j<w;j++){
    if(cnd[i][j]==1) bl++;
    if(cnd[i][j]==2) wh++;
   }
  }
  out.println(bl+" "+wh);
 }
