static class InputReader {
 private InputStream in;
 private byte[] buffer=new byte[1024];
 private int curbuf;
 private int lenbuf;

 public InputReader(InputStream in) {this.in=in; this.curbuf=this.lenbuf=0;}

 public boolean hasNextByte() {
  if(curbuf>=lenbuf){
   curbuf= 0;
   try{
    lenbuf=in.read(buffer);
   }catch(IOException e) {
    throw new InputMismatchException();
   }
   if(lenbuf<=0) return false;
  }
  return true;
 }

 private int readByte(){if(hasNextByte()) return buffer[curbuf++]; else return -1;}

 private boolean isSpaceChar(int c){return !(c>=33&&c<=126);}

 private void skip(){while(hasNextByte()&&isSpaceChar(buffer[curbuf])) curbuf++;}

 public boolean hasNext(){skip(); return hasNextByte();}

 public String next(){
  if(!hasNext()) throw new NoSuchElementException();
  StringBuilder sb=new StringBuilder();
  int b=readByte();
  while(!isSpaceChar(b)){
   sb.appendCodePoint(b);
   b=readByte();
  }
  return sb.toString();
 }

 public int nextInt() {
  if(!hasNext()) throw new NoSuchElementException();
  int c=readByte();
  while (isSpaceChar(c)) c=readByte();
  boolean minus=false;
  if (c=='-') {
   minus=true;
   c=readByte();
  }
  int res=0;
  do{
   if(c<'0'||c>'9') throw new InputMismatchException();
   res=res*10+c-'0';
   c=readByte();
  }while(!isSpaceChar(c));
  return (minus)?-res:res;
 }

 public long nextLong() {
  if(!hasNext()) throw new NoSuchElementException();
  int c=readByte();
  while (isSpaceChar(c)) c=readByte();
  boolean minus=false;
  if (c=='-') {
   minus=true;
   c=readByte();
  }
  long res = 0;
  do{
   if(c<'0'||c>'9') throw new InputMismatchException();
   res=res*10+c-'0';
   c=readByte();
  }while(!isSpaceChar(c));
  return (minus)?-res:res;
 }

 public double nextDouble(){return Double.parseDouble(next());}

 public int[] nextIntArray(int n){
  int[] a=new int[n];
  for(int i=0;i<n;i++) a[i]=nextInt();
  return a;
 }

 public long[] nextLongArray(int n){
  long[] a=new long[n];
  for(int i=0;i<n;i++) a[i]=nextLong();
  return a;
 }

 public char[][] nextCharMap(int n,int m){
  char[][] map=new char[n][m];
  for(int i=0;i<n;i++) map[i]=next().toCharArray();
  return map;
 }
}
}

import java.util.Scanner;

public class Main {

	public static void main(String[] args) {
		// TODO 自動生成されたメソッド・スタブ
		Scanner sc = new Scanner(System.in);

		while(true){
			int w = sc.nextInt();
			int h = sc.nextInt();

			if(w == 0 && h == 0){
				break;
			}

			char matrix[][] = new char[h][w];

			for(int i = 0; i < h; i++){
				String input = sc.next();
				for(int j = 0; j < w; j++){
					matrix[i][j] = input.charAt(j);
				}
			}

//			showMatrix(matrix);
			solve(matrix);
		}
	}

	static void solve(char[][] matrix){
		int h = matrix.length;
		int w = matrix[0].length;

		int[][] resultB = new int[h][w];
		int[][] resultW = new int[h][w];

		int black = 0;
		int white = 0;

		for(int i = 0; i < h; i++){
			for(int j = 0; j < w; j++){
//				if(matrix[i][j] != '.'){
//					continue;
//				}
				if(matrix[i][j] == 'B'){
					isExNeighbor(matrix, resultB, j, i, 'B');
				}
				if(matrix[i][j] == 'W'){
					isExNeighbor(matrix, resultW, j, i, 'W');
				}
			}
		}

		for(int i = 0; i < h; i++){
			for(int j = 0; j < w; j++){
				if(resultB[i][j] == TRUE && resultW[i][j] != TRUE){
					black++;
				}
				else if(resultW[i][j] == TRUE && resultB[i][j] != TRUE){
					white++;
				}
			}
		}


		//debug
//		showMatrix(resultB);
//		showMatrix(resultW);

		System.out.println(black+" "+white);
	}

	static void showMatrix(char[][] matrix){
		for(int i = 0; i < matrix.length; i++){
			for(int j = 0; j < matrix[0].length ; j++){
				System.out.print(matrix[i][j]+" ");
			}
			System.out.println();
		}
		System.out.println();

	}

	static void showMatrix(int[][] matrix){
		for(int i = 0; i < matrix.length; i++){
			for(int j = 0; j < matrix[0].length ; j++){
				System.out.print(matrix[i][j]+" ");
			}
			System.out.println();
		}
		System.out.println();

	}

	static final int TRUE = 2;
	static final int FALSE = 1;
	static final int NOT_VISITED = 0;
	static final int JUDGE = 4;
	static final int DC = 5;
	static void isExNeighbor(char[][] map, int[][] result, int x, int y, char type){
//		System.out.println("x = "+x+" y = "+y);
		int w = map[0].length;
		int h = map.length;

		int dx[] = {1, 0, -1, 0};
		int dy[] = {0, 1, 0, -1};

//		result[y][x] = JUDGE;
		if(map[y][x] == type){
			result[y][x] = DC;
		}
		else {
			result[y][x] = TRUE;
		}

		for(int i = 0; i < 4; i++){
			int tmpX = x + dx[i];
			int tmpY = y + dy[i];

			if(!inRange(tmpX, tmpY, h, w)){
				continue;
			}

			if(map[tmpY][tmpX] == '.' && result[tmpY][tmpX] == NOT_VISITED){
				isExNeighbor(map, result, tmpX, tmpY, type);
			}
		}
	}

	static boolean inRange(int x, int y, int h, int w){
		if(x >= 0 && x < w && y >= 0 && y < h){
			return true;
		}
		else {
			return false;
		}
	}

}

import java.util.Scanner;

public class Main {

	static int h;
	static int w;
	static int cnt = 0;
	static char[][] g;
	static boolean[][] v;
	static boolean wbool;
	static boolean bbool;

	static void dfs(int i, int j) {
		if(v[i][j]) return;
		if(g[i][j] == 'W') {
			wbool = true;
			return;
		}
		if(g[i][j] == 'B') {
			bbool = true;
			return;
		}
		if(g[i][j] != '.') return;
		v[i][j] = true;
		cnt++;
		if(i > 0) dfs(i-1, j);
		if(i < h-1) dfs(i+1, j);
		if(j > 0) dfs(i, j-1);
		if(j < w-1) dfs(i, j+1);
	}

	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		while(true) {
			w = sc.nextInt();
			h = sc.nextInt();
			if(w + h == 0) break;
			g = new char[h+1][w+1];
			v = new boolean[h+1][w+1];
			for(int i = 0; i < h; i++) {
				g[i] = sc.next().toCharArray();
			}

			int hcnt = 0;
			int bcnt = 0;
			for(int i = 0; i < h; i++) {
				for(int j = 0; j < w; j++) {
					if(g[i][j] == '.' && v[i][j] == false) {
						wbool =false;
						bbool = false;
						cnt = 0;
						dfs(i, j);
						if(wbool && !bbool) {
							hcnt += cnt;
						}
						if(bbool && !wbool) {
							bcnt += cnt;
						}
					}
				}
			}
			System.out.println(bcnt + " " + hcnt);
		}

	}

}

import java.util.Arrays;
import java.util.Comparator;
import java.util.LinkedList;
import java.util.PriorityQueue;
import java.util.Queue;
import java.util.Scanner;

public class Main {
  Scanner sc;

  class Point {
    int x, y;

    Point(int x_, int y_) {
      x = x_;
      y = y_;
    }
  }

  int[][] ofs = new int[][] { { 0, 1 }, { 1, 0 }, { -1, 0 }, { 0, -1 } };

  void run() {
    for ( ;; ) {
      int w, h;
      w = ni();
      h = ni();
      if ( w == 0 ) {
        break;
      }

      boolean[][] W = new boolean[h + 2][w + 2];
      boolean[][] B = new boolean[h + 2][w + 2];
      char[][] field = new char[h + 2][w + 2];
      Queue<Point> wq = new LinkedList<Point>();
      Queue<Point> bq = new LinkedList<Point>();
      for ( int i = 1; i <= h; ++i ) {
        char[] str = sc.next().toCharArray();
        for ( int j = 1; j <= w; ++j ) {
          field[ i ][ j ] = str[ j - 1 ];
          if ( field[ i ][ j ] == 'W' ) {
            wq.add( new Point( j, i ) );
          }
          if ( field[ i ][ j ] == 'B' ) {
            bq.add( new Point( j, i ) );
          }
        }
      }

      while (wq.size() > 0) {
        Point e = wq.poll();
        for ( int[] v : ofs ) {
          Point np = new Point( e.x + v[ 0 ], e.y + v[ 1 ] );
          if ( field[ np.y ][ np.x ] == '.' && !W[ np.y ][ np.x ] ) {
            wq.add( np );
            W[ np.y ][ np.x ] |= true;
          }
        }
      }
      while (bq.size() > 0) {
        Point e = bq.poll();
        for ( int[] v : ofs ) {
          Point np = new Point( e.x + v[ 0 ], e.y + v[ 1 ] );
          if ( field[ np.y ][ np.x ] == '.' && !B[ np.y ][ np.x ] ) {
            bq.add( np );
            B[ np.y ][ np.x ] |= true;
          }
        }
      }

      int wc, bc;
      wc = bc = 0;
      for ( int i = 1; i <= h; ++i ) {
        for ( int j = 1; j <= w; ++j ) {
          if ( W[ i ][ j ] && B[ i ][ j ] )
            continue;
          if ( W[ i ][ j ] )
            ++wc;
          if ( B[ i ][ j ] )
            ++bc;
        }
      }

      System.out.println( bc + " " + wc );
    }
  }

  Main() {
    sc = new Scanner( System.in );
  }

  int ni() {
    return sc.nextInt();
  }

  public static void main(String[] args) {
    new Main().run();
  }

  void debug(Object... os) {
    System.err.println( Arrays.deepToString( os ) );
  }

}

