								break;
							}
						}

						if(x < w-1)
						{
							switch(map[y][x+1])
							{
							case 1:
							case 3:
								black++;
								break;
							case 2:
							case 4:
								white++;
								break;
							case 5:
								brank++;
								break;
							}
						}

						if(y < h-1)
						{
							switch(map[y+1][x])
							{
							case 1:
							case 3:
								black++;
								break;
							case 2:
							case 4:
								white++;
								break;
							case 5:
								brank++;
								break;
							}
						}


						if(brank > 0)
						{
							map[y][x] = 5;
							modify = true;
						}
						else if(black == 0 && white > 0 && map[y][x] != 4)
						{
							map[y][x] = 4;
							modify = true;
						}
						else if(black > 0 && white == 0 && map[y][x] != 3)
						{
							map[y][x] = 3;
							modify = true;
						}
						else if(black > 0 && white > 0 && map[y][x] != 5)
						{
							map[y][x] = 5;
							modify = true;
						}
					}
				}
			}

			int black=0, white=0;
			for(y = 0;y < h;y++)
			{
				for(x = 0;x < w;x++)
				{
					if(map[y][x] == 3)
						black++;
					else if(map[y][x] == 4)
						white++;
				}
			}

			System.out.println(black + " " + white);
		}
	}
}

import java.util.Scanner;
public class Main
{
	static int field[][];
	static int white=0,black=0,cnt,fb,fw;
	static int W,H;
	public static void main(String[] args) 
	{
		Scanner in=new Scanner(System.in);
		for(;;)
		{
			W=in.nextInt();
			H=in.nextInt();
			if((W|H)==0)
				return;
			field=new int[H][W];
			for(int i=0;i<H;i++)
			{
				String st=in.next();
				for(int j=0;j<W;j++)
				{
					if(st.charAt(j)=='.')
						field[i][j]=0;
					else if(st.charAt(j)=='W')
						field[i][j]=1;
					else if(st.charAt(j)=='B')
						field[i][j]=2;
				}
			}
			black=0;
			white=0;
			for(int i=0;i<H;i++)
			{
				for(int j=0;j<W;j++)
				{
					cnt=0;
					fb=0;
					fw=0;
					if(field[i][j]==0)
						solve(i,j);
				}
			}
			System.out.println(black+" "+white);
		}	
	}
	public static void solve(int h,int w)
	{
		field[h][w]=3;
		cnt++;
		if(h-1>=0&&field[h-1][w]==1)
			fw=1;
		if(h-1>=0&&field[h-1][w]==2)
			fb=1;
		if(h+1<H&&field[h+1][w]==1)
			fw=1;
		if(h+1<H&&field[h+1][w]==2)
			fb=1;
		if(w-1>=0&&field[h][w-1]==1)
			fw=1;
		if(w-1>=0&&field[h][w-1]==2)
			fb=1;
		if(w+1<W&&field[h][w+1]==1)
			fw=1;
		if(w+1<W&&field[h][w+1]==2)
			fb=1;

		if(h-1>=0&&field[h-1][w]==0)
			solve(h-1,w);
		if(h+1<H&&field[h+1][w]==0)
			solve(h+1,w);
		if(w-1>=0&&field[h][w-1]==0)
			solve(h,w-1);
		if(w+1<W&&field[h][w+1]==0)
			solve(h,w+1);

		if(fw==1&&fb==0)
		{
			white+=cnt;
			cnt=0;
			return;
		}
		else if(fw==0&&fb==1)
		{
			black+=cnt;
			cnt=0;
			return;
		}
		else
		{
			cnt=0;
			return;
		}
	}
}

import java.io.InputStream;
import java.io.IOException;
import java.io.PrintWriter;
import java.util.ArrayDeque;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.Comparator;
import java.util.Deque;
import java.util.InputMismatchException;
import java.util.NoSuchElementException;
import java.math.BigInteger;

public class Main{

static PrintWriter out;
static InputReader ir;

static void solve(){
 for(;;){
  int w=ir.nextInt();
  int h=ir.nextInt();
  if(w==0&&h==0) return;
  char[][] map=ir.nextCharMap(h,w);
  int[][] cnd=new int[h][w];
  for(int i=0;i<h;i++){
   for(int j=0;j<w;j++){
    if(map[i][j]!='.') bfs(i,j,map[i][j]=='B'?1:2,map,cnd);
   }
  }
  int bl=0,wh=0;
  for(int i=0;i<h;i++){
   for(int j=0;j<w;j++){
    if(cnd[i][j]==1) bl++;
    if(cnd[i][j]==2) wh++;
   }
  }
  out.println(bl+" "+wh);
 }
}

static final int[] dd=new int[]{0,-1,0,1};
static Deque<int[]> que=new ArrayDeque<>();

public static boolean in(int x,int y,int h,int w){
 return (x>=0&&y>=0&&x<h&&y<w);
}

public static void bfs(int x,int y,int m,char[][] map,int[][] cnd){
 int h=map.length,w=map[0].length;
 for(int i=0;i<4;i++){
  int nx=x+dd[i],ny=y+dd[i^1];
  if(!in(nx,ny,h,w)) continue;
  if(map[nx][ny]=='.'&&(cnd[nx][ny]&m)==0) que.offerLast(new int[]{nx,ny});
 }
 while(!que.isEmpty()){
  int[] p=que.pollFirst();
  int px=p[0],py=p[1];
  cnd[px][py]|=m;
  for(int i=0;i<4;i++){
   int nx=px+dd[i],ny=py+dd[i^1];
   if(!in(nx,ny,h,w)) continue;
   if(map[nx][ny]=='.'&&(cnd[nx][ny]&m)==0) que.offerLast(new int[]{nx,ny});
  }
 }
}

public static void main(String[] args) throws Exception{
 ir=new InputReader(System.in);
 out=new PrintWriter(System.out);
 solve();
 out.flush();
}

static class InputReader {
 private InputStream in;
 private byte[] buffer=new byte[1024];
 private int curbuf;
 private int lenbuf;

 public InputReader(InputStream in) {this.in=in; this.curbuf=this.lenbuf=0;}

 public boolean hasNextByte() {
  if(curbuf>=lenbuf){
   curbuf= 0;
   try{
    lenbuf=in.read(buffer);
   }catch(IOException e) {
    throw new InputMismatchException();
   }
   if(lenbuf<=0) return false;
  }
  return true;
 }

 private int readByte(){if(hasNextByte()) return buffer[curbuf++]; else return -1;}

 private boolean isSpaceChar(int c){return !(c>=33&&c<=126);}

 private void skip(){while(hasNextByte()&&isSpaceChar(buffer[curbuf])) curbuf++;}

 public boolean hasNext(){skip(); return hasNextByte();}

 public String next(){
  if(!hasNext()) throw new NoSuchElementException();
  StringBuilder sb=new StringBuilder();
  int b=readByte();
  while(!isSpaceChar(b)){
   sb.appendCodePoint(b);
   b=readByte();
  }
  return sb.toString();
 }

 public int nextInt() {
  if(!hasNext()) throw new NoSuchElementException();
  int c=readByte();
  while (isSpaceChar(c)) c=readByte();
  boolean minus=false;
  if (c=='-') {
   minus=true;
   c=readByte();
  }
  int res=0;
  do{
   if(c<'0'||c>'9') throw new InputMismatchException();
   res=res*10+c-'0';
   c=readByte();
  }while(!isSpaceChar(c));
  return (minus)?-res:res;
 }

 public long nextLong() {
  if(!hasNext()) throw new NoSuchElementException();
  int c=readByte();
  while (isSpaceChar(c)) c=readByte();
  boolean minus=false;
  if (c=='-') {
   minus=true;
   c=readByte();
  }
  long res = 0;
  do{
   if(c<'0'||c>'9') throw new InputMismatchException();
   res=res*10+c-'0';
   c=readByte();
  }while(!isSpaceChar(c));
  return (minus)?-res:res;
 }

 public double nextDouble(){return Double.parseDouble(next());}

 public int[] nextIntArray(int n){
  int[] a=new int[n];
  for(int i=0;i<n;i++) a[i]=nextInt();
  return a;
 }

 public long[] nextLongArray(int n){
  long[] a=new long[n];
  for(int i=0;i<n;i++) a[i]=nextLong();
  return a;
 }

 public char[][] nextCharMap(int n,int m){
  char[][] map=new char[n][m];
  for(int i=0;i<n;i++) map[i]=next().toCharArray();
  return map;
 }
}
}

