class Main {

	static boolean touched[][] = new boolean[55][55];
	static char board[][] = new char[55][55];
	static int owner[][] = new int[55][55];
	static int w, h;
	static int dx[] = {1,0,-1,0}, dy[] = {0,1,0,-1};

	public static void main(String args[]){
		Scanner in = new Scanner(System.in);
		for(;;){
			w = in.nextInt();
			h = in.nextInt();
			for(int i=0; i<55; i++)for(int j=0; j<55; j++){
				board[i][j] = '@';
				owner[i][j] = 0;
			}
			for(int i=0; i<h; i++){
				String str = in.next();
				for(int j=0; j<w; j++){
					board[i][j] = str.charAt(j);
				}
			}
			for(int i=0; i<h; i++){
				for(int j=0; j<w; j++){
					if(board[i][j] != '.'){
						for(int ii=0; ii<h; ii++)for(int jj=0;jj<w;jj++) touched[ii][jj] = false;
						for(int k=0; k<4; k++)dfs(j+dx[k], i+dy[k], board[i][j]);
					}
				}
			}
			int black = 0, white = 0;
			for(int i=0; i<h; i++)
				for(int j=0; j<w; j++)
					if(owner[i][j]==1) black++;
					else if(owner[i][j]==2) white++;

			System.out.println(black + " " + white);
		}
	}

	static void dfs(int x, int y, char c){
		if(x<0 || w<=x || y<0 || h<=y) return ;
		touched[y][x] = true;
		if(board[y][x]=='.'){
			owner[y][x] |= (c=='B')?1:2;
			for(int i=0; i<4; i++){
				if(!(x+dx[i]<0 || w<=x+dx[i] || y+dy[i]<0 || h<=y+dy[i]) && !touched[y+dy[i]][x+dx[i]]){
					dfs(x+dx[i], y+dy[i], c);
				}
			}
		}
		return ;
	}

}

import java.util.*;
public class Main { 
	public static boolean[][] wMap;
	public static boolean[][] bMap;
	public static boolean[][] wxMap;
	public static boolean[][] bxMap;
	public static void main(String[] args) {
		Scanner stdIn = new Scanner(System.in);
		while(true) {
			int w = stdIn.nextInt();
			int h = stdIn.nextInt();
			if(w == 0 && h == 0) break;
			wMap = new boolean[h][w];
			bMap = new boolean[h][w];
			wxMap = new boolean[h][w];
			bxMap = new boolean[h][w];
			for(int i = 0; i < h; i++) {
				String tmp = stdIn.next();
				for(int j = 0; j < w; j++) {
					if(tmp.charAt(j) == 'W') {
						wMap[i][j] = true;
					}
					else if(tmp.charAt(j) == 'B') {
						bMap[i][j] = true;
					}
				}
			}

			for(int i = 0; i < h; i++) {
				for(int j = 0; j < w; j++) {
					if(wMap[i][j]) {
						solvW(i+1,j);
						solvW(i-1,j);
						solvW(i,j+1);
						solvW(i,j-1);
					}
					if(bMap[i][j]) {
						solvB(i+1,j);
						solvB(i-1,j);
						solvB(i,j+1);
						solvB(i,j-1);
					}
				}
			}
			int wc = 0;
			int bc = 0;
			for(int i = 0; i < h; i++) {
				for(int j = 0; j < w; j++) {
					if(wxMap[i][j] && !bxMap[i][j] &&checkW(i,j)) {
						wc++;
					}
					else if(bxMap[i][j] && !wxMap[i][j] && checkB(i,j)) {
						bc++;
					}
				}
			}
			System.out.println(bc + " "+ wc);

		}
	}
	public static void solvW(int i, int j) {
		if(i < 0 || j < 0 || i >= wMap.length || j >= wMap[i].length) return;
		if(wMap[i][j] || bMap[i][j]) return;
		if(wxMap[i][j]) return;
		boolean isChange = false;
		if(i != 0 && (wxMap[i-1][j] || wMap[i-1][j])) {
			wxMap[i][j] = true;
			isChange = true;
		}
		else if(j != 0 && (wxMap[i][j-1] || wMap[i][j-1])) {
			wxMap[i][j] = true;
			isChange = true;
		}
		else if(i != wxMap.length-1 && (wxMap[i+1][j] || wMap[i+1][j])) {
			wxMap[i][j] = true;
			isChange = true;
		}
		else if(j != wxMap[i].length-1 && (wxMap[i][j+1] || wMap[i][j+1])) {
			wxMap[i][j] = true;
			isChange = true;
		}

		if(!isChange) return;
		solvW(i+1,j);
		solvW(i-1,j);
		solvW(i,j+1);
		solvW(i,j-1);
	}
	public static void solvB(int i, int j) {
		if(i < 0 || j < 0 || i >= bMap.length || j >= bMap[i].length) return;
		if(wMap[i][j] || bMap[i][j]) return;
		if(bxMap[i][j]) return;
		boolean isChange = false;
		if(i != 0 && (bxMap[i-1][j] || bMap[i-1][j])) {
			bxMap[i][j] = true;
			isChange = true;
		}
		else if(j != 0 && (bxMap[i][j-1] || bMap[i][j-1])) {
			bxMap[i][j] = true;
			isChange = true;
		}
		else if(i != bxMap.length-1 && (bxMap[i+1][j] || bMap[i+1][j])) {
			bxMap[i][j] = true;
			isChange = true;
		}
		else if(j != bxMap[i].length-1 && (bxMap[i][j+1] || bMap[i][j+1])) {
			bxMap[i][j] = true;
			isChange = true;
		}

		if(!isChange) return;
		solvB(i+1,j);
		solvB(i-1,j);
		solvB(i,j+1);
		solvB(i,j-1);
	}

	public static boolean checkW(int i, int j) {
		if(i != 0 && bMap[i-1][j]) {
			return false;
		}
		else if(j != 0 && bMap[i][j-1]) {
			return false;
		}
		else if(i != bMap.length-1 && bMap[i+1][j]) {
			return false;
		}
		else if(j != bMap[i].length-1 && bMap[i][j+1]) {
			return false;
		}
		return true;
	}
	public static boolean checkB(int i, int j) {
		if(i != 0 && wMap[i-1][j]) {
			return false;
		}
		else if(j != 0 && wMap[i][j-1]) {
			return false;
		}
		else if(i != wMap.length-1 && wMap[i+1][j]) {
			return false;
		}
		else if(j != wMap[i].length-1 && wMap[i][j+1]) {
			return false;
		}
		return true;
	}
}

import java.util.Scanner;

//Surrounding Area
public class Main{

	int w, h;
	char[][] m;
	int[][] move = {{-1,0}, {0,1}, {1,0}, {0,-1}};
	boolean[][] white, black;

	void fw(int i, int j){
		if(white[i][j]||m[i][j]!='.')return;
		white[i][j] = true;
		for(int k=0;k<4;k++){
			int ni = i+move[k][0], nj = j+move[k][1];
			if(0<=ni&&ni<h&&0<=nj&&nj<w)fw(ni, nj);
		}
	}
	void fb(int i, int j){
		if(black[i][j]||m[i][j]!='.')return;
		black[i][j] = true;
		for(int k=0;k<4;k++){
			int ni = i+move[k][0], nj = j+move[k][1];
			if(0<=ni&&ni<h&&0<=nj&&nj<w)fb(ni, nj);
		}
	}

	void run(){
		Scanner sc = new Scanner(System.in);
		for(;;){
			w = sc.nextInt(); h = sc.nextInt();
			if((w|h)==0)break;
			m = new char[h][];
			for(int i=0;i<h;i++)m[i]=sc.next().toCharArray();
			white = new boolean[h][w]; black = new boolean[h][w];
			for(int i=0;i<h;i++)for(int j=0;j<w;j++){
				if(m[i][j]=='W'){
					for(int k=0;k<4;k++){
						int ni = i+move[k][0], nj = j+move[k][1];
						if(0<=ni&&ni<h&&0<=nj&&nj<w)fw(ni, nj);
					}
				}
				if(m[i][j]=='B'){
					for(int k=0;k<4;k++){
						int ni = i+move[k][0], nj = j+move[k][1];
						if(0<=ni&&ni<h&&0<=nj&&nj<w)fb(ni, nj);
					}
				}
			}
			int cw = 0, cb = 0;
			for(int i=0;i<h;i++)for(int j=0;j<w;j++){
				if(m[i][j]!='.'||white[i][j]&&black[i][j]||!white[i][j]&&!black[i][j])continue;
				if(white[i][j])cw++;
				else cb++;
			}
			System.out.println(cb+" "+cw);
		}
	}

	public static void main(String[] args) {
		new Main().run();
	}
}

// AOJ 2014 "土地囲い" (ICPC模擬国内予選 2007 Problem C)
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        while (true) {
            int w = sc.nextInt();
            int h = sc.nextInt();
            if (w == 0) {
                break;
            }

            StringBuffer[] field = new StringBuffer[h];
            for (int i = 0; i < field.length; i++) {
                field[i] = new StringBuffer(sc.next());
            }

            for (int i = 0; i < field.length; i++) {
                for (int j = 0; j < field[i].length(); j++) {
                    if (field[i].charAt(j) == 'W') {
                        dfs(field, j, i, 'w');
                    } else if (field[i].charAt(j) == 'B') {
                        dfs(field, j, i, 'b');
                    }
                }
            }

            int bArea = 0;
            int wArea = 0;
            for (int i = 0; i < field.length; i++) {
                for (int j = 0; j < field[i].length(); j++) {
                    if (field[i].charAt(j) == 'b') {
                        bArea++;
                    } else if (field[i].charAt(j) == 'w') {
                        wArea++;
                    }
                }
            }
            System.out.println(bArea + " " + wArea);
        }
    }

    public static void dfs(StringBuffer[] field, int x, int y, char c) {
        int[] dx = {0, 1, 0, -1};
        int[] dy = {1, 0, -1, 0};

        for (int i = 0; i < dx.length; i++) {
            int nx = x + dx[i];
            int ny = y + dy[i];

            if (0 <= ny && ny < field.length && 0 <= nx && nx < field[ny].length()) {
                if (field[ny].charAt(nx) == '.') {
                    field[ny].setCharAt(nx, c);
                    dfs(field, nx, ny, c);
                } else if ((field[ny].charAt(nx) == 'b' && c == 'w')
                || (field[ny].charAt(nx) == 'w' && c == 'b')) {
                    field[ny].setCharAt(nx, 'g');
                    dfs(field, nx, ny, c);
                }
            }
        }
    }
}

