static class InputReader {
 private InputStream in;
 private byte[] buffer=new byte[1024];
 private int curbuf;
 private int lenbuf;

 public InputReader(InputStream in) {this.in=in; this.curbuf=this.lenbuf=0;}

 public boolean hasNextByte() {
  if(curbuf>=lenbuf){
   curbuf= 0;
   try{
    lenbuf=in.read(buffer);
   }catch(IOException e) {
    throw new InputMismatchException();
   }
   if(lenbuf<=0) return false;
  }
  return true;
 }

 private int readByte(){if(hasNextByte()) return buffer[curbuf++]; else return -1;}

 private boolean isSpaceChar(int c){return !(c>=33&&c<=126);}

 private void skip(){while(hasNextByte()&&isSpaceChar(buffer[curbuf])) curbuf++;}

 public boolean hasNext(){skip(); return hasNextByte();}

 public String next(){
  if(!hasNext()) throw new NoSuchElementException();
  StringBuilder sb=new StringBuilder();
  int b=readByte();
  while(!isSpaceChar(b)){
   sb.appendCodePoint(b);
   b=readByte();
  }
  return sb.toString();
 }

 public int nextInt() {
  if(!hasNext()) throw new NoSuchElementException();
  int c=readByte();
  while (isSpaceChar(c)) c=readByte();
  boolean minus=false;
  if (c=='-') {
   minus=true;
   c=readByte();
  }
  int res=0;
  do{
   if(c<'0'||c>'9') throw new InputMismatchException();
   res=res*10+c-'0';
   c=readByte();
  }while(!isSpaceChar(c));
  return (minus)?-res:res;
 }

 public long nextLong() {
  if(!hasNext()) throw new NoSuchElementException();
  int c=readByte();
  while (isSpaceChar(c)) c=readByte();
  boolean minus=false;
  if (c=='-') {
   minus=true;
   c=readByte();
  }
  long res = 0;
  do{
   if(c<'0'||c>'9') throw new InputMismatchException();
   res=res*10+c-'0';
   c=readByte();
  }while(!isSpaceChar(c));
  return (minus)?-res:res;
 }

 public double nextDouble(){return Double.parseDouble(next());}

 public int[] nextIntArray(int n){
  int[] a=new int[n];
  for(int i=0;i<n;i++) a[i]=nextInt();
  return a;
 }

 public long[] nextLongArray(int n){
  long[] a=new long[n];
  for(int i=0;i<n;i++) a[i]=nextLong();
  return a;
 }

 public char[][] nextCharMap(int n,int m){
  char[][] map=new char[n][m];
  for(int i=0;i<n;i++) map[i]=next().toCharArray();
  return map;
 }
}
}

import java.util.Scanner;

class Main {
    private static void mark(int x, int y, int flag, char[][] data, int[][] map) {
        for (int i = x + 1; i < data.length; i++) {
            if (data[i][y] == '.' && (map[i][y] & flag) == 0) {
                map[i][y] |= flag;
                mark(i, y, flag, data, map);
            } else {
                break;
            }
        }
        for (int i = x - 1; i >= 0; i--) {
            if (data[i][y] == '.' && (map[i][y] & flag) == 0) {
                map[i][y] |= flag;
                mark(i, y, flag, data, map);
            } else {
                break;
            }
        }
        for (int j = y + 1; j < data[0].length; j++) {
            if (data[x][j] == '.' && (map[x][j] & flag) == 0) {
                map[x][j] |= flag;
                mark(x, j, flag, data, map);
            } else {
                break;
            }
        }
        for (int j = y - 1; j >= 0; j--) {
            if (data[x][j] == '.' && (map[x][j] & flag) == 0) {
                map[x][j] |= flag;
                mark(x, j, flag, data, map);
            } else {
                break;
            }
        }
    }

    private static void solve() {
        final Scanner scanner = new Scanner(System.in);

        while (true) {
            final int w = scanner.nextInt();
            final int h = scanner.nextInt();
            if (w == 0 && h == 0) {
                break;
            }

            final char[][] data = new char[h][];
            final int[][] map = new int[h][w];
            for (int i = 0; i < h; i++) {
                    data[i] = scanner.next().toCharArray();
            }
            for (int i = 0; i < h; i++) {
                for (int j = 0; j < w; j++) {
                    switch (data[i][j]) {
                        case 'B':
                            mark(i, j, 0x1, data, map);
                            break;
                        case 'W':
                            mark(i, j, 0x2, data, map);
                            break;
                        default:
                            break;
                    }
                }
            }
            int black = 0;
            int white = 0;
            for (int i = 0; i < h; i++) {
                for (int j = 0; j < w; j++) {
                    switch (map[i][j]) {
                        case 0x1:
                            black++;
                            break;
                        case 0x2:
                            white++;
                            break;
                        default:
                            break;
                    }
                }
            }
            System.out.println(black + " " + white);
        }
    }

    public static void main(String... args) {
        solve();
    }
}

import java.util.Scanner;

public class Main {

	static int width, height, count=0;
	static char[][] field;
	static boolean[][] v;
	static boolean wbool, bbool;

	static void visit(int x, int y) {
		if(field[x][y]=='B') bbool=true;
		if(field[x][y]=='W') wbool=true;
		if(field[x][y]!='.') return;
		if(v[x][y]) return;
		v[x][y]=true;
		count++;
		if(x<height-1)
			visit(x+1, y);
		if(x>0)
			visit(x-1, y);
		if(y<width-1)
			visit(x, y+1);
		if(y>0)
			visit(x, y-1);
	}

	public static void main(String[] args) {
		try(Scanner sc = new Scanner(System.in)){
			while(sc.hasNext()) {
				width=sc.nextInt();
				height=sc.nextInt();
				if(width==0) break;
				field=new char[height+1][width+1];
				v=new boolean[height+1][width+1];
				for(int i=0; i<height; i++) {
					String str=sc.next();
					for(int j=0; j<width; j++) {
						field[i][j]=str.charAt(j);
					}
				}
				int wcount=0, bcount=0;
				for(int i=0; i<height; i++) {
					for(int j=0; j<width; j++) {
						if(v[i][j]) {
							continue;
						}
						if(field[i][j]!='.') {
							continue;
						}
						wbool=false;
						bbool=false;
						count=0;
						visit(i, j);
						if(wbool && ! bbool) {
							wcount+=count;
						}
						else if(bbool && ! wbool) {
							bcount+=count;
						}
					}
				}
				System.out.println(bcount+" "+wcount);
			}


		}
	}
}

import java.util.Scanner;
public class Main
{
	static int field[][];
	static int which[][];
	static int white,black,cnt;
	static int W,H;
	static int dx[]={0,1,-1,0};
	static int dy[]={1,0,0,-1};
	public static void main(String[] args) 
	{
		Scanner in=new Scanner(System.in);
		for(;;)
		{
			W=in.nextInt();
			H=in.nextInt();
			if((W|H)==0)
				return;
			field=new int[H][W];
			which=new int[H][W];
			for(int i=0;i<H;i++)
			{
				String st=in.next();
				for(int j=0;j<W;j++)
				{
					if(st.charAt(j)=='.')
						field[i][j]=0;
					else if(st.charAt(j)=='W')
						field[i][j]=1;
					else if(st.charAt(j)=='B')
						field[i][j]=2;
				}
			}
			black=0;
			white=0;
			for(int i=0;i<H;i++)
			{
				for(int j=0;j<W;j++)
				{
					if(field[i][j]==1)//白で未訪問かつ白色なら
						dfs(i,j,"w",0);
					if(field[i][j]==2)//黒で未訪問かつ黒色なら
						dfs(i,j,"b",0);
				}
			}
			for(int i=0;i<H;i++)
			{
				for(int j=0;j<W;j++)
				{
					if(which[i][j]==1)
						white++;
					else if(which[i][j]==2)
						black++;
				}
			}
			System.out.println(black+" "+white);
		}	
	}
	public static void dfs(int h,int w,String s,int n)
	{
		if(n!=0)
		{
			if(s.equals("w"))
			{
				field[h][w]=3;//白で訪問済み
				which[h][w]+=1;//白なら1足す
			}
			else if(s.equals("b"))
			{
				field[h][w]=4;//黒で訪問済み
				which[h][w]+=2;//黒なら2足す
			}
		}
		for(int i=0;i<4;i++)
		{
			int nh=h+dy[i];
			int nw=w+dx[i];
			if(s.equals("w"))
				if(nh>=0&&nh<H&&nw>=0&&nw<W&&(field[nh][nw]==0||field[nh][nw]==4))//白で未訪問なら
					dfs(nh,nw,s,n+1);
			if(s.equals("b"))
				if(nh>=0&&nh<H&&nw>=0&&nw<W&&(field[nh][nw]==0||field[nh][nw]==3))//黒で未訪問なら
					dfs(nh,nw,s,n+1);
		}
	}
}

import java.util.*;
public class Main{
    public static void main(String[] args){
	new Main().run();
    }
    Scanner sc = new Scanner(System.in);

    char[][] map;
    int[][] round; // 0:未, 1:黒, 2:白, -1:黒でも白でもない
    int w, h;
    int B, W;


    void run(){
	while(sc.hasNext()){
	    w = sc.nextInt();
	    h = sc.nextInt();
	    if(w==0 && h==0) break;

	    map = new char[h][w];
	    round = new int[h][w];

	    for(int i=0; i<h; i++){
		String s = sc.next();
		for(int k=0; k<w; k++)
		    map[i] = s.toCharArray();
	    }

	    for(int i=0; i<h; i++)
		for(int k=0; k<w; k++)
		    if(map[i][k]=='B' || map[i][k]=='W')
			solve(0, 0, map[i][k]);

	    count();
	    //show();
	    System.out.println(B+" "+W);
	}
    }

    void solve(int a, int b, char c){
	if(a<0 || a>=h || b<0 || b>=w) return;
	if(round[a][b]==-1) return;

	if(map[a][b]=='B' || map[a][b]=='W'){
	    c = map[a][b];
	    round[a][b] = -1;
	    solve(a+1, b, c);
	    solve(a-1, b, c);
	    solve(a, b+1, c);
	    solve(a, b-1, c);
	}
	else if(round[a][b]==0){
	    round[a][b] = c=='B' ? 1 : 2;
	    solve(a+1, b, c);
	    solve(a-1, b, c);
	    solve(a, b+1, c);
	    solve(a, b-1, c);
	}
	else if( (round[a][b]==1 && c=='W') || (round[a][b]==2 && c=='B')){
	    round[a][b] = -1;
	    solve(a+1, b, c);
	    solve(a-1, b, c);
	    solve(a, b+1, c);
	    solve(a, b-1, c);
	}
    }

    void count(){
	B = 0;
	W = 0;
	for(int i=0; i<h; i++)
	    for(int k=0; k<w; k++)
		if(round[i][k]==1) B++;
		else if(round[i][k]==2) W++;
    }

    void show(){
	for(int i=0; i<h; i++){
	    for(int k=0; k<w; k++)
		System.out.print(round[i][k]+" ");
	    System.out.println();
	}
    }
}

