static class InputReader {
 private InputStream in;
 private byte[] buffer=new byte[1024];
 private int curbuf;
 private int lenbuf;

 public InputReader(InputStream in) {this.in=in; this.curbuf=this.lenbuf=0;}

 public boolean hasNextByte() {
  if(curbuf>=lenbuf){
   curbuf= 0;
   try{
    lenbuf=in.read(buffer);
   }catch(IOException e) {
    throw new InputMismatchException();
   }
   if(lenbuf<=0) return false;
  }
  return true;
 }

 private int readByte(){if(hasNextByte()) return buffer[curbuf++]; else return -1;}

 private boolean isSpaceChar(int c){return !(c>=33&&c<=126);}

 private void skip(){while(hasNextByte()&&isSpaceChar(buffer[curbuf])) curbuf++;}

 public boolean hasNext(){skip(); return hasNextByte();}

 public String next(){
  if(!hasNext()) throw new NoSuchElementException();
  StringBuilder sb=new StringBuilder();
  int b=readByte();
  while(!isSpaceChar(b)){
   sb.appendCodePoint(b);
   b=readByte();
  }
  return sb.toString();
 }

 public int nextInt() {
  if(!hasNext()) throw new NoSuchElementException();
  int c=readByte();
  while (isSpaceChar(c)) c=readByte();
  boolean minus=false;
  if (c=='-') {
   minus=true;
   c=readByte();
  }
  int res=0;
  do{
   if(c<'0'||c>'9') throw new InputMismatchException();
   res=res*10+c-'0';
   c=readByte();
  }while(!isSpaceChar(c));
  return (minus)?-res:res;
 }

 public long nextLong() {
  if(!hasNext()) throw new NoSuchElementException();
  int c=readByte();
  while (isSpaceChar(c)) c=readByte();
  boolean minus=false;
  if (c=='-') {
   minus=true;
   c=readByte();
  }
  long res = 0;
  do{
   if(c<'0'||c>'9') throw new InputMismatchException();
   res=res*10+c-'0';
   c=readByte();
  }while(!isSpaceChar(c));
  return (minus)?-res:res;
 }

 public double nextDouble(){return Double.parseDouble(next());}

 public int[] nextIntArray(int n){
  int[] a=new int[n];
  for(int i=0;i<n;i++) a[i]=nextInt();
  return a;
 }

 public long[] nextLongArray(int n){
  long[] a=new long[n];
  for(int i=0;i<n;i++) a[i]=nextLong();
  return a;
 }

 public char[][] nextCharMap(int n,int m){
  char[][] map=new char[n][m];
  for(int i=0;i<n;i++) map[i]=next().toCharArray();
  return map;
 }
}
}

public class Main {

	private static void simulate(char[][] d) {
		java.util.Queue<Pair> lis = new java.util.LinkedList<Pair>();

		for (int i = 0; i < d.length; ++i) {
			for (int j = 0; j < d[i].length; ++j) {
				if (d[i][j] == 'B') {
					lis.add(new Pair(i, j - 1));
					lis.add(new Pair(i, j + 1));
					lis.add(new Pair(i - 1, j));
					lis.add(new Pair(i + 1, j));
				}
			}
		}
		while (!lis.isEmpty()) {
			final Pair p = lis.remove();
			final int i = p.i;
			final int j = p.j;
			if (i < 0 || i >= d.length) continue ;
			if (j < 0 || j >= d[i].length) continue ;
			if (d[i][j] == '.') {
				d[i][j] = 'b';
			} else continue ;
			lis.add(new Pair(i, j - 1));
			lis.add(new Pair(i, j + 1));
			lis.add(new Pair(i - 1, j));
			lis.add(new Pair(i + 1, j));
		}

		lis.clear();		
		for (int i = 0; i < d.length; ++i) {
			for (int j = 0; j < d[i].length; ++j) {
				if (d[i][j] == 'W') {
					lis.add(new Pair(i, j - 1));
					lis.add(new Pair(i, j + 1));
					lis.add(new Pair(i - 1, j));
					lis.add(new Pair(i + 1, j));
				}
			}
		}
		while (!lis.isEmpty()) {
			final Pair p = lis.remove();
			final int i = p.i;
			final int j = p.j;
			if (i < 0 || i >= d.length) continue ;
			if (j < 0 || j >= d[i].length) continue ;
			if (d[i][j] == '.') {
				d[i][j] = 'w';
			} else if (d[i][j] == 'b') {
				d[i][j] = 'g';
			} else continue ;
			lis.add(new Pair(i, j - 1));
			lis.add(new Pair(i, j + 1));
			lis.add(new Pair(i - 1, j));
			lis.add(new Pair(i + 1, j));
		}
	}

	private static int countif(char[][] d, char ch) {
		int rvalue = 0;
		for (int i = 0; i < d.length; ++i) {
			for (int j = 0; j < d[i].length; ++j) {
				if (d[i][j] == ch) {
					rvalue += 1;
				}
			}
		}
		return rvalue;
	}

	public static void main(String[] args) {
		try (final java.util.Scanner sc = new java.util.Scanner(System.in)) {
			for (;;) {
				final int w = sc.nextInt();
				final int h = sc.nextInt();
				if (w == 0 && h == 0) break ;
				final char[][] d = new char[h][];
				for (int i = 0; i < h; ++i) {
					d[i] = sc.next().toCharArray();
				}
				simulate(d);
				System.out.printf("%d %d\n", countif(d, 'b'), countif(d, 'w'));
			}
		}
	}

	private static class Pair {
		private int i;
		private int j;

		private Pair(int i, int j) {
			this.i = i;
			this.j = j;
		}
	}

}

import java.util.Scanner;

public class Main {

	char [][] data;
	int black,white,temp;
	char state;

	final int []xdir = {0,1,0,-1};
	final int []ydir = {1,0,-1,0};

	private void solve(int x, int y){
		if(data[y][x] == '*'){
			return;
		}
		if(data[y][x] == 'W'){
			if(state == 'N')
				state = 'W';
			if(state == 'B')
				state = 'D';
			return;
		}
		if(data[y][x] == 'B'){
			if(state == 'N')
				state = 'B';
			if(state == 'W')
				state = 'D';
			return;
		}
		data[y][x] = '*';
		temp++;
		for(int i=0; i < 4; i++)
			solve(x + xdir[i], y + ydir[i]);
	}

	private void doIt() {
		Scanner sc = new Scanner(System.in);
		while(true){
			int w = sc.nextInt();
			int h = sc.nextInt();
			if(w == 0 && h == 0){
				break;
			}
			data = new char[h + 2][w + 2];
			black = 0;
			white = 0;
			temp = 0;
			state = 'N';
			for(int i=0; i <= h + 1; i++){
				data[i][0] = '*';
				data[i][w+1] = '*';
			}
			for(int i =0; i <= w + 1; i++){
				data[0][i] = '*';
				data[h+1][i] = '*';
			}
			for(int i=1; i < h + 1; i++){
				String str = sc.next();
				for(int j = 1; j < w + 1; j++){
					data[i][j] = str.charAt(j - 1);
				}
			}

			for(int i=1; i < h + 1; i++){
				for(int j=1; j < w + 1; j++){
					if(data[i][j] == '.'){
						solve(j,i);
						//System.out.println(temp + " " + black + " " + white+ " "+  i + " " + j);
						switch(state){
						case 'B':
							black += temp;
							temp = 0;
							state = 'N';
							break;
						case 'W':
							white += temp;
							temp = 0;
							state = 'N';
							break;
						default:
							temp = 0;
							state = 'N';
						}
					}
				}
			}
			System.out.println(black + " " + white);

			//debug
//			for(int i=0; i <= h + 1; i++){
//				for(int j = 0; j <= w + 1; j++){
//					System.out.print(data[i][j] + " ");
//				}
//				System.out.println("");
//			}
		}

	}

	public static void main(String[] args) {
		Main obj = new Main();
		obj.doIt();
	}

}

import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        StringBuilder sb = new StringBuilder();
        while (true) {
            int w = sc.nextInt();
            int h = sc.nextInt();
            if (w == 0 && h == 0) {
                break;
            }
            char[][] field = new char[h][];
            for (int i = 0; i < h; i++) {
                field[i] = sc.next().toCharArray();
            }
            HashMap<Character, Boolean> counts = new HashMap<>();
            int whiteCount = 0;
            int blackCount = 0;
            char current = (char)('W' + 1);
            for (int i = 0; i < h; i++) {
                for (int j = 0; j < w; j++) {
                    if (field[i][j] == '.') {
                        Result result = new Result();
                        check(i, j, current, field, result);
                        if (result.white && !result.black) {
                            counts.put(current, true);
                        } else if (!result.white && result.black) {
                            counts.put(current, false);
                        }
                        current = (char)(current + 1);
                    }
                }
            }
            for (int i = 0; i < h; i++) {
                for (int j = 0; j < w; j++) {
                    if (counts.containsKey(field[i][j])) {
                        if (counts.get(field[i][j])) {
                            whiteCount++;
                        } else {
                            blackCount++;
                        }
                    }
                }
            }
            sb.append(blackCount).append(" ").append(whiteCount).append("\n");
        }
        System.out.print(sb);
    }

    static void check(int r, int c, char mark, char[][] field, Result result) {
        if (field[r][c] == 'W') {
            result.white = true;
            return;
        }
        if (field[r][c] == 'B') {
            result.black = true;
            return;
        }
        if (field[r][c] != '.') {
            return;
        }
        field[r][c] = mark;
        if (r != 0) {
            check(r - 1, c, mark, field, result);
        }
        if (r < field.length - 1) {
            check(r + 1, c, mark, field, result);
        }
        if (c > 0) {
            check(r, c - 1, mark, field, result);
        }
        if (c < field[r].length - 1) {
            check(r, c + 1, mark, field, result);
        }
    }

    static class Result {
        boolean white = false;
        boolean black = false;
    }
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayDeque;
import java.util.Deque;

import static java.lang.Integer.parseInt;

/**
 * Surrounding Area
 */
public class Main {

	static final int BLACK = 1;
	static final int WHITE = 2;
	static final int[][] HV = {{-1, 0}, {1, 0}, {0, -1}, {0, 1}};

	public static void main(String[] args) throws IOException {
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		String line;
		String[] words;

		while ((line = br.readLine()) != null && !line.isEmpty()) {

			int w, h;
			w = parseInt(line.substring(0, line.indexOf(' ')));
			h = parseInt(line.substring(line.indexOf(' ') + 1));
			if ((w | h) == 0) break;

			char[][] map = new char[h + 2][w + 2];
			int[][] check = new int[h + 2][w + 2];

			for (int i = 1; i <= h; i++) {
				line = br.readLine();
				for (int j = 1; j <= w; j++) {
					map[i][j] = line.charAt(j - 1);
					if (map[i][j] == 'B' || map[i][j] == 'W') {
						check[i][j] = BLACK + WHITE;
					}
				}
			}

			for (int i = 1; i <= h; i++) {
				for (int j = 1; j <= w; j++) {
					if (map[i][j] == 'B' || map[i][j] == 'W') {
						int marker = 0;
						if (map[i][j] == 'B') {
							marker = BLACK;
						} else {
							marker = WHITE;
						}
						Deque<int[]> queue = new ArrayDeque<>();
						queue.offer(new int[]{i, j});
						while (!queue.isEmpty()) {
							int[] p = queue.poll();
							for (int k = 0; k < HV.length; k++) {
								int ny = p[0] + HV[k][0];
								int nx = p[1] + HV[k][1];
								if (1 <= ny && ny <= h
										&& 1 <= nx && nx <= w
										&& map[ny][nx] == '.'
										&& (check[ny][nx] & marker) == 0) {
									queue.offer(new int[]{ny, nx});
									check[ny][nx] += marker;
								}
							}
						}
					}
				}
			}

			int black = 0;
			int white = 0;
			for (int i = 1; i <= h; i++) {
				for (int j = 1; j <= w; j++) {
					if (check[i][j] == BLACK) {
						black++;
					} else if (check[i][j] == WHITE) {
						white++;
					}
				}
			}
			System.out.println(black + " " + white);

		} //end while
	} //end main
}

