public class Main{

    static final Reader sc = new Reader();
    static final PrintWriter out = new PrintWriter(System.out,false);

    public static void main(String[] args) throws Exception {
        while(true){
	        int w = sc.nextInt();
    	    int h = sc.nextInt();
    	    if(w==0 && h==0){
    	    	break;
    	    }
    	    String[] str = new String[h];
    	    boolean[][] judge = new boolean[w][h];
    	    for(int i=0;i<h;i++){
    	    	str[i] = sc.next();
    	    }
    	    int[] dx = {-1,0,1,0};
    	    int[] dy = {0,-1,0,1};
    	    ArrayList<Point> list1 = new ArrayList<Point>();
    	    ArrayList<Point> list2 = new ArrayList<Point>();
    	    int bcount = 0;
    	    int wcount = 0;
    	    for(int i=0;i<h;i++){
    	    	for(int j=0;j<w;j++){
    	    		if(str[i].charAt(j)!='.'){
    	    			continue;
    	    		}
    	    		Point point = new Point(j,i);
    	    		boolean black = false;
    	    		boolean white = false;
    	    		list1.add(point);
    	    		while(true){
    	    			while(!list1.isEmpty()){
    	    				point = list1.get(0);
    	    				list1.remove(0);
    	    				for(int k=0;k<4;k++){
    	    					if(point.x==0 && k==0){
    	    						continue;
    	    					}
    	    					if(point.x==w-1&&k==2){
    	    						continue;
    	    					}
    	    					if(point.y==0&&k==1){
    	    						continue;
    	    					}
    	    					if(point.y==h-1&&k==3){
    	    						continue;
    	    					}
    	    					if(str[point.y+dy[k]].charAt(point.x+dx[k])=='.' && !judge[point.x+dx[k]][point.y+dy[k]]){
    	    						judge[point.x+dx[k]][point.y+dy[k]] = true;
    	    						Point p = new Point(point.x+dx[k],point.y+dy[k]);
    	    						list2.add(p);
    	    					}
    	    					if(str[point.y+dy[k]].charAt(point.x+dx[k])=='B'){
    	    						black = true;
    	    					}
    	    					if(str[point.y+dy[k]].charAt(point.x+dx[k])=='W'){
    	    						white = true;
    	    					}
    	    				}
    	    			}
    	    			if(list2.isEmpty()){
    	    				break;
    	    			}
    	    			list1.clear();
    	    			for(int k=0;k<list2.size();k++){
    	    				list1.add(list2.get(k));
    	    			}
    	    			list2.clear();
    	    		}
    	    		for(int k=0;k<w;k++){
    	    			Arrays.fill(judge[k],false);
    	    		}
    	    		if(black && !white){
    	    			bcount++;
    	    		}
    	    		if(!black && white){
    	    			wcount++;
    	    		}
    	    	}
    	    }
    	    out.println(bcount+" "+wcount);
    	    out.flush();
    	}
        sc.close();
        out.close();
    }

    static void trace(Object... o) { System.out.println(Arrays.deepToString(o));}
