}

public static void dfs(int x,int y,int m){
 cnd[x][y]|=m;
 for(int i=0;i<4;i++){
  int nx=x+dd[i],ny=y+dd[i^1];
  if(!in(nx,ny)) continue;
  if(map[nx][ny]=='.'&&(cnd[nx][ny]&m)==0) dfs(nx,ny,m);
 }
}

